goog.provide("goog.storage.mechanism.PrefixedMechanismTest");goog.setTestOnly("goog.storage.mechanism.PrefixedMechanismTest");goog.require("goog.storage.mechanism.HTML5LocalStorage");goog.require("goog.storage.mechanism.PrefixedMechanism");goog.require("goog.storage.mechanism.mechanismSeparationTester");goog.require("goog.storage.mechanism.mechanismSharingTester");goog.require("goog.testing.jsunit");var submechanism=null;function setUp(){submechanism=new goog.storage.mechanism.HTML5LocalStorage;if(submechanism.isAvailable()){mechanism=new goog.storage.mechanism.PrefixedMechanism(submechanism,"test");mechanism_shared=new goog.storage.mechanism.PrefixedMechanism(submechanism,"test");mechanism_separate=new goog.storage.mechanism.PrefixedMechanism(submechanism,"test2")}}function tearDown(){if(!!mechanism){mechanism.clear();mechanism=null}if(!!mechanism_shared){mechanism_shared.clear();mechanism_shared=null}if(!!mechanism_separate){mechanism_separate.clear();mechanism_separate=null}}function testAvailability(){if(submechanism.isAvailable()){assertNotNull(mechanism);assertNotNull(mechanism_shared);assertNotNull(mechanism_separate)}}