goog.provide("goog.string.path");goog.require("goog.array");goog.require("goog.string");goog.string.path.baseName=function(path){var i=path.lastIndexOf("/")+1;return path.slice(i)};goog.string.path.basename=goog.string.path.baseName;goog.string.path.dirname=function(path){var i=path.lastIndexOf("/")+1,head=path.slice(0,i);if(!/^\/+$/.test(head)){head=head.replace(/\/+$/,"")}return head};goog.string.path.extension=function(path){var separator=".",baseName=goog.string.path.baseName(path).replace(/\.+/g,separator),separatorIndex=baseName.lastIndexOf(separator);return 0>=separatorIndex?"":baseName.substr(separatorIndex+1)};goog.string.path.join=function(var_args){for(var path=arguments[0],i=1,arg;i<arguments.length;i++){arg=arguments[i];if(goog.string.startsWith(arg,"/")){path=arg}else if(""==path||goog.string.endsWith(path,"/")){path+=arg}else{path+="/"+arg}}return path};goog.string.path.normalizePath=function(path){if(""==path){return"."}var initialSlashes="";if(goog.string.startsWith(path,"/")){initialSlashes="/";if(goog.string.startsWith(path,"//")&&!goog.string.startsWith(path,"///")){initialSlashes="//"}}for(var parts=path.split("/"),newParts=[],i=0,part;i<parts.length;i++){part=parts[i];if(""==part||"."==part){continue}if(".."!=part||!initialSlashes&&!newParts.length||".."==goog.array.peek(newParts)){newParts.push(part)}else{newParts.pop()}}var returnPath=initialSlashes+newParts.join("/");return returnPath||"."};goog.string.path.split=function(path){var head=goog.string.path.dirname(path),tail=goog.string.path.baseName(path);return[head,tail]};