goog.provide("goog.style.bidi");goog.require("goog.dom");goog.require("goog.style");goog.require("goog.userAgent");goog.style.bidi.getScrollLeft=function(element){var isRtl=goog.style.isRightToLeft(element);if(isRtl&&goog.userAgent.GECKO){return-element.scrollLeft}else if(isRtl&&!(goog.userAgent.EDGE_OR_IE&&goog.userAgent.isVersionOrHigher("8"))){var overflowX=goog.style.getComputedOverflowX(element);if("visible"==overflowX){return element.scrollLeft}else{return element.scrollWidth-element.clientWidth-element.scrollLeft}}return element.scrollLeft};goog.style.bidi.getOffsetStart=function(element){element=element;var offsetLeftForReal=element.offsetLeft,bestParent=element.offsetParent;if(!bestParent&&"fixed"==goog.style.getComputedPosition(element)){bestParent=goog.dom.getOwnerDocument(element).documentElement}if(!bestParent){return offsetLeftForReal}if(goog.userAgent.GECKO){var borderWidths=goog.style.getBorderBox(bestParent);offsetLeftForReal+=borderWidths.left}else if(goog.userAgent.isDocumentModeOrHigher(8)&&!goog.userAgent.isDocumentModeOrHigher(9)){var borderWidths=goog.style.getBorderBox(bestParent);offsetLeftForReal-=borderWidths.left}if(goog.style.isRightToLeft(bestParent)){var elementRightOffset=offsetLeftForReal+element.offsetWidth;return bestParent.clientWidth-elementRightOffset}return offsetLeftForReal};goog.style.bidi.setScrollOffset=function(element,offsetStart){offsetStart=Math.max(offsetStart,0);if(!goog.style.isRightToLeft(element)){element.scrollLeft=offsetStart}else if(goog.userAgent.GECKO){element.scrollLeft=-offsetStart}else if(!(goog.userAgent.EDGE_OR_IE&&goog.userAgent.isVersionOrHigher("8"))){element.scrollLeft=element.scrollWidth-offsetStart-element.clientWidth}else{element.scrollLeft=offsetStart}};goog.style.bidi.setPosition=function(elem,left,top,isRtl){if(!goog.isNull(top)){elem.style.top=top+"px"}if(isRtl){elem.style.right=left+"px";elem.style.left=""}else{elem.style.left=left+"px";elem.style.right=""}};