goog.provide("goog.style.webkitScrollbarsTest");goog.setTestOnly("goog.style.webkitScrollbarsTest");goog.require("goog.asserts");goog.require("goog.style");goog.require("goog.styleScrollbarTester");goog.require("goog.testing.ExpectedFailures");goog.require("goog.testing.jsunit");goog.require("goog.userAgent");var expectedFailures;function setUpPage(){expectedFailures=new goog.testing.ExpectedFailures}function tearDown(){expectedFailures.handleTearDown();goog.asserts.assert(testScrollbarWidth)}function testScrollBarWidth_webkitScrollbar(){expectedFailures.expectFailureFor(!goog.userAgent.WEBKIT);try{var width=goog.style.getScrollbarWidth();assertEquals("Scrollbar width should be 16",16,width)}catch(e){expectedFailures.handleException(e)}}function testScrollBarWidth_webkitScrollbarWithCustomClass(){expectedFailures.expectFailureFor(!goog.userAgent.WEBKIT);try{var customWidth=goog.style.getScrollbarWidth("otherScrollBar");assertEquals("Custom width should be 10",10,customWidth)}catch(e){expectedFailures.handleException(e)}}