goog.provide("goog.styleScrollbarTester");goog.require("goog.dom");goog.require("goog.dom.TagName");goog.require("goog.style");goog.require("goog.testing.asserts");goog.setTestOnly("goog.styleScrollbarTester");function testScrollbarWidth(){var width=goog.style.getScrollbarWidth();assertTrue(0<width);var outer=goog.dom.getElement("test-scrollbarwidth"),inner=goog.dom.getElementsByTagNameAndClass(goog.dom.TagName.DIV,null,outer)[0];assertTrue("should have a scroll bar",hasVerticalScroll(outer));assertTrue("should have a scroll bar",hasHorizontalScroll(outer));goog.style.setStyle(outer,"width","100%");assertTrue("should have a scroll bar",hasVerticalScroll(outer));assertFalse("should not have a scroll bar",hasHorizontalScroll(outer));var innerAbsoluteWidth=inner.offsetWidth;goog.style.setStyle(outer,"width",innerAbsoluteWidth+width+"px");assertTrue("should have a scroll bar",hasVerticalScroll(outer));assertFalse("should not have a scroll bar",hasHorizontalScroll(outer));goog.style.setStyle(outer,"width",innerAbsoluteWidth+width-1+"px");assertTrue("should have a scroll bar",hasVerticalScroll(outer));assertTrue("should have a scroll bar",hasHorizontalScroll(outer))}function hasVerticalScroll(el){return 0!=el.clientWidth&&0<el.offsetWidth-el.clientWidth}function hasHorizontalScroll(el){return 0!=el.clientHeight&&0<el.offsetHeight-el.clientHeight}