goog.provide("goog.testing.AsyncTestCaseTest");goog.setTestOnly("goog.testing.AsyncTestCaseTest");goog.require("goog.debug.Error");goog.require("goog.testing.AsyncTestCase");goog.require("goog.testing.asserts");goog.require("goog.testing.jsunit");function testControlBreakingExceptionThrown(){var asyncTestCase=new goog.testing.AsyncTestCase;try{asyncTestCase.doAsyncError()}catch(e){assertTrue(e.isControlBreakingException);assertEquals("",e.message)}var errorMessage1="Error message 1";try{asyncTestCase.doAsyncError(errorMessage1)}catch(e){assertTrue(e.isControlBreakingException);assertEquals(errorMessage1,e.message)}var errorMessage2="Error message 2";try{var error=new goog.debug.Error(errorMessage2);asyncTestCase.doAsyncError(error)}catch(e){assertTrue(e.isControlBreakingException);assertEquals(errorMessage2,e.message)}}function testMaybeFailTestEarly(){var message="Error in setUpPage().",asyncTestCase=new goog.testing.AsyncTestCase;asyncTestCase.setUpPage=function(){throw Error(message)};asyncTestCase.addNewTest("test",function(){assertTrue(!0)});asyncTestCase.runTests();window.setTimeout(function(){assertFalse(asyncTestCase.isSuccess());var errors=asyncTestCase.getResult().errors;assertEquals(1,errors.length);assertEquals(message,errors[0].message)},2*asyncTestCase.stepTimeout)}