goog.provide("goog.testing.ContinuationTestCaseTest");goog.setTestOnly("goog.testing.ContinuationTestCaseTest");goog.require("goog.events");goog.require("goog.events.EventTarget");goog.require("goog.testing.ContinuationTestCase");goog.require("goog.testing.MockClock");goog.require("goog.testing.PropertyReplacer");goog.require("goog.testing.TestCase");goog.require("goog.testing.jsunit");var testCase=new goog.testing.ContinuationTestCase("Continuation Test Case");testCase.autoDiscoverTests();if("undefined"!=typeof G_testRunner){G_testRunner.initialize(testCase)}var clock=new goog.testing.MockClock,count=0,stubs=new goog.testing.PropertyReplacer;function setUpPage(){count=testCase.getCount()}function setUp(){waitForTimeout(function(){assertEquals(count,testCase.getCount())},0);clock.reset()}function tearDown(){clock.uninstall();stubs.reset();waitForTimeout(function(){assertTrue(testCase.now()>=testCase.startTime_)},0)}function installMockClock(){clock.install();stubs.set(goog.testing.ContinuationTestCase.Step,"protectedClearTimeout_",window.clearTimeout);stubs.set(goog.testing.ContinuationTestCase.Step,"protectedSetTimeout_",window.setTimeout)}function getSampleStep(){return new goog.testing.ContinuationTestCase.Step("test",function(){})}function getSampleTest(){var setupStep=new goog.testing.TestCase.Test("setup",function(){}),testStep=new goog.testing.TestCase.Test("test",function(){}),teardownStep=new goog.testing.TestCase.Test("teardown",function(){});return new goog.testing.ContinuationTestCase.ContinuationTest(setupStep,testStep,teardownStep)}function testStepWaiting(){var step=getSampleStep();assertTrue(step.waiting)}function testStepSetTimeout(){installMockClock();var step=getSampleStep(),timeoutReached=!1;step.setTimeout(function(){timeoutReached=!0},100);clock.tick(50);assertFalse(timeoutReached);clock.tick(50);assertTrue(timeoutReached)}function testStepClearTimeout(){var step=new goog.testing.ContinuationTestCase.Step("test",function(){}),timeoutReached=!1;step.setTimeout(function(){timeoutReached=!0},100);clock.tick(50);assertFalse(timeoutReached);step.clearTimeout();clock.tick(50);assertFalse(timeoutReached)}function testTestPhases(){var test=getSampleTest();assertEquals("setup",test.getCurrentPhase()[0].name);test.cancelCurrentPhase();assertEquals("test",test.getCurrentPhase()[0].name);test.cancelCurrentPhase();assertEquals("teardown",test.getCurrentPhase()[0].name);test.cancelCurrentPhase();assertNull(test.getCurrentPhase())}function testTestSetError(){var test=getSampleTest(),error1=new Error("Oh noes!"),error2=new Error("B0rken.");assertNull(test.getError());test.setError(error1);assertEquals(error1,test.getError());test.setError(error2);assertEquals("Once an error has been set, it should not be overwritten.",error1,test.getError())}function testAddStep(){for(var test=getSampleTest(),step=getSampleStep(),i=0;3>i;i++){assertEquals(1,test.getCurrentPhase().length);test.addStep(step);assertEquals(2,test.getCurrentPhase().length);assertEquals(step,test.getCurrentPhase()[1]);test.cancelCurrentPhase()}assertNull(test.getCurrentPhase())}function testCancelTestPhase(){var test=getSampleTest();test.cancelTestPhase();assertEquals("teardown",test.getCurrentPhase()[0].name);test=getSampleTest();test.cancelCurrentPhase();test.cancelTestPhase();assertEquals("teardown",test.getCurrentPhase()[0].name);test=getSampleTest();test.cancelTestPhase();test.cancelTestPhase();assertEquals("teardown",test.getCurrentPhase()[0].name)}function testWaitForTimeout(){var reachedA=!1,reachedB=!1,reachedC=!1;waitForTimeout(function a(){reachedA=!0;assertTrue("A must be true at callback a.",reachedA);assertFalse("B must be false at callback a.",reachedB);assertFalse("C must be false at callback a.",reachedC)},10);waitForTimeout(function b(){reachedB=!0;assertTrue("A must be true at callback b.",reachedA);assertTrue("B must be true at callback b.",reachedB);assertFalse("C must be false at callback b.",reachedC)},20);waitForTimeout(function c(){reachedC=!0;assertTrue("A must be true at callback c.",reachedA);assertTrue("B must be true at callback c.",reachedB);assertTrue("C must be true at callback c.",reachedC)},20);assertFalse("a",reachedA);assertFalse("b",reachedB);assertFalse("c",reachedC)}function testWaitForEvent(){var et=new goog.events.EventTarget,eventFired=!1;goog.events.listen(et,"testPrefire",function(){eventFired=!0;et.dispatchEvent("test")});waitForEvent(et,"test",function(){assertTrue(eventFired)});et.dispatchEvent("testPrefire")}function testWaitForCondition(){var counter=0;waitForCondition(function(){return 2<=++counter},function(){assertEquals(2,counter)},10,200)}function testOutOfOrderWaits(){var counter=0;waitForTimeout(function(){assertEquals(3,++counter)},200);waitForTimeout(function(){assertEquals(1,++counter)},0);waitForTimeout(function(){assertEquals(2,++counter)},100)}var testObj;function testCrazyNestedWaitFunction(){testObj={lock:!0,et:new goog.events.EventTarget,steps:0};waitForTimeout(handleTimeout,10);waitForEvent(testObj.et,"test",handleEvent);waitForCondition(condition,handleCondition,1)}function handleTimeout(){testObj.steps++;assertEquals("handleTimeout should be called first.",1,testObj.steps);waitForTimeout(fireEvent,10)}function fireEvent(){testObj.steps++;assertEquals("fireEvent should be called second.",2,testObj.steps);testObj.et.dispatchEvent("test")}function handleEvent(){testObj.steps++;assertEquals("handleEvent should be called third.",3,testObj.steps);testObj.lock=!1}function condition(){return!testObj.lock}function handleCondition(){assertFalse(testObj.lock);testObj.steps++;assertEquals("handleCondition should be called last.",4,testObj.steps)}