goog.provide("goog.testing.events.EventMatcherTest");goog.setTestOnly("goog.testing.events.EventMatcherTest");goog.require("goog.events.Event");goog.require("goog.testing.events.EventMatcher");goog.require("goog.testing.jsunit");function testEventMatcher(){var matcher=new goog.testing.events.EventMatcher("foo");assertFalse(matcher.matches(void 0));assertFalse(matcher.matches(null));assertFalse(matcher.matches({type:"foo"}));assertFalse(matcher.matches(new goog.events.Event("bar")));assertTrue(matcher.matches(new goog.events.Event("foo")));var FooEvent=function(){goog.events.Event.call(this,"foo")};goog.inherits(FooEvent,goog.events.Event);assertTrue(matcher.matches(new FooEvent))}