goog.provide("goog.testing.fs.BlobTest");goog.setTestOnly("goog.testing.fs.BlobTest");goog.require("goog.dom");goog.require("goog.testing.fs.Blob");goog.require("goog.testing.jsunit");var hasArrayBuffer=goog.isDef(goog.global.ArrayBuffer);function testInput(){var blob=new goog.testing.fs.Blob;assertEquals("",blob.toString());assertEquals(0,blob.size);blob=new goog.testing.fs.Blob("\u8CC7");assertEquals("\u8CC7",blob.toString());assertEquals(3,blob.size);if(!hasArrayBuffer){return}blob=new goog.testing.fs.Blob([new Uint8Array([232,179,135])]);assertEquals("\u8CC7",blob.toString());assertEquals(3,blob.size);for(var data=goog.dom.getWindow().atob("iVBORw0KGgo="),arr=[],i=0;i<data.length;i++){arr.push(data.charCodeAt(i))}blob=new goog.testing.fs.Blob([new Uint8Array(arr)]);assertArrayEquals([137,80,78,71,13,10,26,10],blob.data_);assertEquals(8,blob.size);blob=new goog.testing.fs.Blob(["\u8CC7","\xE9"]);assertEquals("\u8CC7\xE9",blob.toString());assertEquals(5,blob.size);blob=new goog.testing.fs.Blob([new Uint8Array([232,179,135]),"\xE9"]);assertEquals("\u8CC7\xE9",blob.toString());assertEquals(5,blob.size)}function testType(){var blob=new goog.testing.fs.Blob;assertEquals("",blob.type);blob=new goog.testing.fs.Blob("foo bar baz","text/plain");assertEquals("text/plain",blob.type)}function testSlice(){var blob=new goog.testing.fs.Blob("abcdef");assertEquals("bc",blob.slice(1,3).toString());assertEquals("def",blob.slice(3,10).toString());assertEquals("abcd",blob.slice(0,-2).toString());assertEquals("",blob.slice(10,1).toString());assertEquals("",blob.slice(10,30).toString());assertEquals("b",blob.slice(-5,2).toString());assertEquals("abcdef",blob.slice().toString());assertEquals("abc",blob.slice(void 0,3).toString());assertEquals("def",blob.slice(3).toString());assertEquals("text/plain",blob.slice(1,2,"text/plain").type);blob=new goog.testing.fs.Blob("ab\u8CC7cd");assertEquals("ab\u8CC7",blob.slice(0,5).toString());assertEquals("\u8CC7",blob.slice(2,5).toString());assertEquals("\u8CC7cd",blob.slice(2,10).toString());assertEquals("ab",blob.slice(0,-5).toString());assertEquals("c",blob.slice(-2,-1).toString());assertEquals("\u8CC7c",blob.slice(-5,-1).toString());assertEquals("ab\u8CC7cd",blob.slice().toString());assertEquals("ab\u8CC7",blob.slice(void 0,5).toString());assertEquals("cd",blob.slice(5).toString());assertArrayEquals([232],blob.slice(2,3).data_)}function testToArrayBuffer(){if(!hasArrayBuffer){return}var blob=new goog.testing.fs.Blob("\u8CC7"),buf=new ArrayBuffer(this.size),arr=new Uint8Array(buf);arr=[232,179,135];assertElementsEquals(buf,blob.toArrayBuffer())}function testToDataUrl(){var blob=new goog.testing.fs.Blob("\u8CC7","text");assertEquals("data:text;base64,6LOH",blob.toDataUrl())}