goog.provide("goog.testing.MockControlTest");goog.setTestOnly("goog.testing.MockControlTest");goog.require("goog.testing.Mock");goog.require("goog.testing.MockControl");goog.require("goog.testing.jsunit");function MockMock(){this.replayCalled=!1;this.resetCalled=!1;this.verifyCalled=!1;this.tearDownCalled=!1}MockMock.prototype.$replay=function(){this.replayCalled=!0};MockMock.prototype.$reset=function(){this.resetCalled=!0};MockMock.prototype.$verify=function(){this.verifyCalled=!0};function setUp(){var mock=new goog.testing.Mock(MockMock)}function testAdd(){var mockMock=new MockMock,control=new goog.testing.MockControl;assertEquals(mockMock,control.addMock(mockMock))}function testReplayAll(){var mockMock1=new MockMock,mockMock2=new MockMock,mockMockExcluded=new MockMock,control=new goog.testing.MockControl;control.addMock(mockMock1);control.addMock(mockMock2);control.$replayAll();assertTrue(mockMock1.replayCalled);assertTrue(mockMock2.replayCalled);assertFalse(mockMockExcluded.replayCalled)}function testResetAll(){var mockMock1=new MockMock,mockMock2=new MockMock,mockMockExcluded=new MockMock,control=new goog.testing.MockControl;control.addMock(mockMock1);control.addMock(mockMock2);control.$resetAll();assertTrue(mockMock1.resetCalled);assertTrue(mockMock2.resetCalled);assertFalse(mockMockExcluded.resetCalled)}function testVerifyAll(){var mockMock1=new MockMock,mockMock2=new MockMock,mockMockExcluded=new MockMock,control=new goog.testing.MockControl;control.addMock(mockMock1);control.addMock(mockMock2);control.$verifyAll();assertTrue(mockMock1.verifyCalled);assertTrue(mockMock2.verifyCalled);assertFalse(mockMockExcluded.verifyCalled)}function testTearDownAll(){var mockMock1=new MockMock,mockMock2=new MockMock,mockMockExcluded=new MockMock;mockMock2.$tearDown=function(){this.tearDownCalled=!0};mockMockExcluded.$tearDown=function(){this.tearDownCalled=!0};var control=new goog.testing.MockControl;control.addMock(mockMock1);control.addMock(mockMock2);control.$tearDown();assertTrue(mockMock2.tearDownCalled);assertFalse(mockMock1.tearDownCalled);assertFalse(mockMockExcluded.tearDownCalled)}