goog.provide("goog.testing.MockStorageTest");goog.setTestOnly("goog.testing.MockStorageTest");goog.require("goog.testing.MockStorage");goog.require("goog.testing.jsunit");var instance;function setUp(){instance=new goog.testing.MockStorage}function testMockStorage(){assertEquals(0,instance.length);instance.setItem("foo","bar");assertEquals(1,instance.length);assertEquals("bar",instance.getItem("foo"));assertEquals("foo",instance.key(0));instance.setItem("foo","baz");assertEquals("baz",instance.getItem("foo"));instance.setItem("goo","gl");assertEquals(2,instance.length);assertEquals("gl",instance.getItem("goo"));assertEquals("goo",instance.key(1));assertNull(instance.getItem("poogle"));instance.removeItem("foo");assertEquals(1,instance.length);assertEquals("goo",instance.key(0));instance.setItem("a",12);assertEquals("12",instance.getItem("a"));instance.setItem("b",!1);assertEquals("false",instance.getItem("b"));instance.setItem("c",{a:1,b:12});assertEquals("[object Object]",instance.getItem("c"));instance.clear();assertEquals(0,instance.length);instance.setItem("emptyString","");assertEquals("",instance.getItem("emptyString"));instance.setItem("isNull",null);assertEquals("null",instance.getItem("isNull"));instance.setItem("isUndefined",void 0);assertEquals("undefined",instance.getItem("isUndefined"));instance.setItem("","empty key");assertEquals("empty key",instance.getItem(""));assertEquals(4,instance.length)}