goog.provide("goog.testing.singletonTest");goog.setTestOnly("goog.testing.singletonTest");goog.require("goog.testing.asserts");goog.require("goog.testing.jsunit");goog.require("goog.testing.singleton");function testGetInstance(){function SingletonClass(){}goog.addSingletonGetter(SingletonClass);var s1=SingletonClass.getInstance(),s2=SingletonClass.getInstance();assertEquals("second getInstance call returns the same instance",s1,s2);goog.testing.singleton.reset();var s3=SingletonClass.getInstance();assertNotEquals("getInstance returns a new instance after reset",s1,s3)}