goog.setTestOnly("goog.testing.style");goog.provide("goog.testing.style");goog.require("goog.dom");goog.require("goog.math.Rect");goog.require("goog.style");goog.testing.style.intersects=function(element,otherElement){var elementRect=goog.style.getBounds(element),otherElementRect=goog.style.getBounds(otherElement);return goog.math.Rect.intersects(elementRect,otherElementRect)};goog.testing.style.hasVisibleDimensions=function(element){var elSize=goog.style.getSize(element),shortest=elSize.getShortest();if(0>=shortest){return!1}return!0};goog.testing.style.isVisible=function(element){var visibilityStyle=goog.testing.style.getAvailableStyle_(element,"visibility"),displayStyle=goog.testing.style.getAvailableStyle_(element,"display");return"hidden"!=visibilityStyle&&"none"!=displayStyle};goog.testing.style.isOnScreen=function(el){var doc=goog.dom.getDomHelper(el).getDocument(),viewport=goog.style.getVisibleRectForElement(doc.body),viewportRect=goog.math.Rect.createFromBox(viewport);return goog.dom.contains(doc,el)&&goog.style.getBounds(el).intersects(viewportRect)};goog.testing.style.getAvailableStyle_=function(element,style){return goog.style.getComputedStyle(element,style)||goog.style.getCascadedStyle(element,style)||goog.style.getStyle(element,style)};