goog.provide("goog.testing.ui.rendererassertsTest");goog.setTestOnly("goog.testing.ui.rendererassertsTest");goog.require("goog.testing.TestCase");goog.require("goog.testing.asserts");goog.require("goog.testing.jsunit");goog.require("goog.testing.ui.rendererasserts");goog.require("goog.ui.ControlRenderer");function setUp(){goog.testing.TestCase.getActiveTestCase().failOnUnreportedAsserts=!1}function testSuccess(){function GoodRenderer(){}goog.testing.ui.rendererasserts.assertNoGetCssClassCallsInConstructor(GoodRenderer)}function testFailure(){function BadRenderer(){goog.ui.ControlRenderer.call(this);this.myClass=this.getCssClass()}goog.inherits(BadRenderer,goog.ui.ControlRenderer);var ex=assertThrows("Expected assertNoGetCssClassCallsInConstructor to fail.",function(){goog.testing.ui.rendererasserts.assertNoGetCssClassCallsInConstructor(BadRenderer)});assertTrue("Expected assertNoGetCssClassCallsInConstructor to throw a"+" jsunit exception",ex.isJsUnitException);assertContains("getCssClass",ex.message)}