goog.provide("goog.testing.ui.styleTest");goog.setTestOnly("goog.testing.ui.styleTest");goog.require("goog.dom");goog.require("goog.testing.TestCase");goog.require("goog.testing.jsunit");goog.require("goog.testing.ui.style");var refPath="style_reference.html";goog.testing.ui.style.writeReferenceFrame(refPath);function setUp(){goog.testing.TestCase.getActiveTestCase().failOnUnreportedAsserts=!1}function testCorrect(){var el=goog.dom.getFirstElementChild(goog.dom.getElement("correct"));goog.testing.ui.style.assertStructureMatchesReference(el,"reference")}function testMissingClass(){var el=goog.dom.getFirstElementChild(goog.dom.getElement("missing-class")),e=assertThrows(function(){goog.testing.ui.style.assertStructureMatchesReference(el,"reference")});assertContains("all reference classes",e.message)}function testExtraClass(){var el=goog.dom.getFirstElementChild(goog.dom.getElement("extra-class"));goog.testing.ui.style.assertStructureMatchesReference(el,"reference")}function testMissingChild(){var el=goog.dom.getFirstElementChild(goog.dom.getElement("missing-child")),e=assertThrows(function(){goog.testing.ui.style.assertStructureMatchesReference(el,"reference")});assertContains("same number of children",e.message)}function testExtraChild(){var el=goog.dom.getFirstElementChild(goog.dom.getElement("extra-child")),e=assertThrows(function(){goog.testing.ui.style.assertStructureMatchesReference(el,"reference")});assertContains("same number of children",e.message)}