goog.provide("goog.ui.ac");goog.require("goog.ui.ac.ArrayMatcher");goog.require("goog.ui.ac.AutoComplete");goog.require("goog.ui.ac.InputHandler");goog.require("goog.ui.ac.Renderer");goog.ui.ac.createSimpleAutoComplete=function(data,input,opt_multi,opt_useSimilar){var matcher=new goog.ui.ac.ArrayMatcher(data,!opt_useSimilar),renderer=new goog.ui.ac.Renderer,inputHandler=new goog.ui.ac.InputHandler(null,null,!!opt_multi),autoComplete=new goog.ui.ac.AutoComplete(matcher,renderer,inputHandler);inputHandler.attachAutoComplete(autoComplete);inputHandler.attachInputs(input);return autoComplete};