goog.provide("goog.ui.ac.CachingMatcherTest");goog.setTestOnly("goog.ui.ac.CachingMatcherTest");goog.require("goog.testing.MockControl");goog.require("goog.testing.jsunit");goog.require("goog.testing.mockmatchers");goog.require("goog.ui.ac.CachingMatcher");ignoreArgument=goog.testing.mockmatchers.ignoreArgument;goog.async.Throttle=function(fn,time,self){this.fn=fn;this.time=time;this.self=self;this.numFires=0};goog.async.Throttle.prototype.fire=function(){this.numFires++};goog.async.Throttle.prototype.permitOne=function(){if(0==this.numFires){return}this.fn.call(this.self);this.numFires=0};var mockControl,mockMatcher,mockHandler,matcher;function setUp(){mockControl=new goog.testing.MockControl;mockMatcher={requestMatchingRows:mockControl.createFunctionMock("requestMatchingRows")};mockHandler=mockControl.createFunctionMock("matchHandler");matcher=new goog.ui.ac.CachingMatcher(mockMatcher)}function tearDown(){mockControl.$tearDown()}function testLocalThenRemoteMatch(){mockHandler("foo",[]);mockControl.$replayAll();matcher.requestMatchingRows("foo",12,mockHandler);mockControl.$verifyAll();mockControl.$resetAll();mockHandler("foo",["foo1","foo2"],ignoreArgument);mockMatcher.requestMatchingRows("foo",100,ignoreArgument).$does(function(token,maxResults,matchHandler){matchHandler("foo",["foo1","foo2","bar3"])});mockControl.$replayAll();matcher.throttledTriggerBaseMatch_.permitOne();mockControl.$verifyAll();mockControl.$resetAll()}function testCacheSize(){matcher.setMaxCacheSize(4);mockHandler("foo",[]);mockHandler("foo",["foo111","foo222"],ignoreArgument);mockMatcher.requestMatchingRows("foo",100,ignoreArgument).$does(function(token,maxResults,matchHandler){matchHandler("foo",["foo111","foo222","bar333"])});mockControl.$replayAll();matcher.requestMatchingRows("foo",12,mockHandler);matcher.throttledTriggerBaseMatch_.permitOne();mockControl.$verifyAll();mockControl.$resetAll();mockHandler("foo1",["foo111"]);mockControl.$replayAll();matcher.requestMatchingRows("foo1",12,mockHandler);mockControl.$verifyAll();mockControl.$resetAll();mockHandler("foo11",["foo111"]);mockHandler("foo11",["foo111","foo112","foo113","foo114"],ignoreArgument);mockMatcher.requestMatchingRows("foo11",100,ignoreArgument).$does(function(token,maxResults,matchHandler){matchHandler("foo11",["foo111","foo112","foo113","foo114"])});mockControl.$replayAll();matcher.requestMatchingRows("foo11",12,mockHandler);matcher.throttledTriggerBaseMatch_.permitOne();mockControl.$verifyAll();mockControl.$resetAll();mockHandler("foo11",[]);mockControl.$replayAll();matcher.requestMatchingRows("foo11",12,mockHandler);mockControl.$verifyAll();mockControl.$resetAll()}function testClearCache(){mockHandler("foo",[]);mockHandler("foo",["foo111","foo222"],ignoreArgument);mockMatcher.requestMatchingRows("foo",100,ignoreArgument).$does(function(token,maxResults,matchHandler){matchHandler("foo",["foo111","foo222","bar333"])});mockControl.$replayAll();matcher.requestMatchingRows("foo",12,mockHandler);matcher.throttledTriggerBaseMatch_.permitOne();mockControl.$verifyAll();mockControl.$resetAll();mockHandler("foo1",["foo111"]);mockControl.$replayAll();matcher.requestMatchingRows("foo1",12,mockHandler);mockControl.$verifyAll();mockControl.$resetAll();matcher.clearCache();mockHandler("foo11",[]);mockControl.$replayAll();matcher.requestMatchingRows("foo11",12,mockHandler);mockControl.$verifyAll();mockControl.$resetAll()}function testSimilarMatchingDoesntReorderResults(){mockHandler("ba",[]);mockHandler("ba",["bar","baz","bam"],ignoreArgument);mockMatcher.requestMatchingRows("ba",100,ignoreArgument).$does(function(token,maxResults,matchHandler){matchHandler("ba",["bar","baz","bam"])});mockControl.$replayAll();matcher.requestMatchingRows("ba",12,mockHandler);matcher.throttledTriggerBaseMatch_.permitOne();mockControl.$verifyAll();mockControl.$resetAll();mockHandler("bad",["bar","baz","bam"]);mockHandler("bad",["bar","baz","bam","bad","badder","baddest"],ignoreArgument);mockMatcher.requestMatchingRows("bad",100,ignoreArgument).$does(function(token,maxResults,matchHandler){matchHandler("bad",["bad","badder","baddest"])});mockControl.$replayAll();matcher.requestMatchingRows("bad",12,mockHandler);matcher.throttledTriggerBaseMatch_.permitOne();mockControl.$verifyAll();mockControl.$resetAll();mockHandler("badd",["badder","baddest"]);mockControl.$replayAll();matcher.requestMatchingRows("badd",12,mockHandler);mockControl.$verifyAll();mockControl.$resetAll()}function testSetThrottleTime(){assertEquals(150,matcher.throttledTriggerBaseMatch_.time);matcher.setThrottleTime(234);assertEquals(234,matcher.throttledTriggerBaseMatch_.time)}function testSetBaseMatcherMaxMatches(){mockHandler("foo",[]);mockMatcher.requestMatchingRows("foo",789,ignoreArgument);mockControl.$replayAll();matcher.setBaseMatcherMaxMatches();matcher.requestMatchingRows("foo",12,mockHandler)}function testSetLocalMatcher(){function sillyMatcher(token,maxMatches,rows){rows=rows.concat([]);rows.sort();return rows}mockHandler("foo",[]);mockHandler("foo",["a","b","c"],ignoreArgument);mockMatcher.requestMatchingRows("foo",100,ignoreArgument).$does(function(token,maxResults,matchHandler){matchHandler("foo",["b","a","c"])});mockControl.$replayAll();matcher.setLocalMatcher(sillyMatcher);matcher.requestMatchingRows("foo",12,mockHandler);matcher.throttledTriggerBaseMatch_.permitOne();mockControl.$verifyAll();mockControl.$resetAll()}