goog.provide("goog.ui.ac.RichRemote");goog.require("goog.ui.ac.AutoComplete");goog.require("goog.ui.ac.Remote");goog.require("goog.ui.ac.Renderer");goog.require("goog.ui.ac.RichInputHandler");goog.require("goog.ui.ac.RichRemoteArrayMatcher");goog.ui.ac.RichRemote=function(url,input,opt_multi,opt_useSimilar){var customRenderer={renderRow:function(row,token,node){return row.data.render(node,token)}},renderer=new goog.ui.ac.Renderer(null,customRenderer);this.renderer_=renderer;var matcher=new goog.ui.ac.RichRemoteArrayMatcher(url,!opt_useSimilar);this.matcher_=matcher;var inputhandler=new goog.ui.ac.RichInputHandler(null,null,!!opt_multi,300);goog.ui.ac.AutoComplete.call(this,matcher,renderer,inputhandler);inputhandler.attachAutoComplete(this);inputhandler.attachInputs(input)};goog.inherits(goog.ui.ac.RichRemote,goog.ui.ac.Remote);goog.ui.ac.RichRemote.prototype.setRowFilter=function(rowFilter){this.matcher_.setRowFilter(rowFilter)};