goog.provide("goog.ui.ColorButtonRenderer");goog.require("goog.asserts");goog.require("goog.dom.classlist");goog.require("goog.functions");goog.require("goog.ui.ColorMenuButtonRenderer");goog.ui.ColorButtonRenderer=function(){goog.ui.ColorButtonRenderer.base(this,"constructor");this.createDropdown=goog.functions.NULL};goog.inherits(goog.ui.ColorButtonRenderer,goog.ui.ColorMenuButtonRenderer);goog.addSingletonGetter(goog.ui.ColorButtonRenderer);goog.ui.ColorButtonRenderer.CSS_CLASS=goog.getCssName("goog-color-button");goog.ui.ColorButtonRenderer.prototype.createCaption=function(content,dom){var caption=goog.ui.ColorButtonRenderer.base(this,"createCaption",content,dom);goog.asserts.assert(caption);goog.dom.classlist.add(caption,goog.ui.ColorButtonRenderer.CSS_CLASS);return caption};goog.ui.ColorButtonRenderer.prototype.initializeDom=function(button){goog.ui.ColorButtonRenderer.base(this,"initializeDom",button);goog.dom.classlist.add(goog.asserts.assert(button.getElement()),goog.ui.ColorButtonRenderer.CSS_CLASS)};