goog.provide("goog.ui.ContainerScrollerTest");goog.setTestOnly("goog.ui.ContainerScrollerTest");goog.require("goog.dom");goog.require("goog.testing.MockClock");goog.require("goog.testing.events");goog.require("goog.testing.jsunit");goog.require("goog.ui.Container");goog.require("goog.ui.ContainerScroller");var sandbox,sandboxHtml,container,mockClock,scroller;function setUpPage(){sandbox=goog.dom.getElement("sandbox");sandboxHtml=sandbox.innerHTML}function setUp(){container=new goog.ui.Container;container.decorate(sandbox);container.getElement().scrollTop=0;mockClock=new goog.testing.MockClock(!0);scroller=null}function tearDown(){container.dispose();if(scroller){scroller.dispose()}mockClock.tick(1e3);mockClock.uninstall();sandbox.innerHTML=sandboxHtml}function testHighlightFirstStaysAtTop(){scroller=new goog.ui.ContainerScroller(container);container.getChildAt(0).setHighlighted(!0);assertEquals(0,container.getElement().scrollTop)}function testHighlightSecondStaysAtTop(){scroller=new goog.ui.ContainerScroller(container);container.getChildAt(1).setHighlighted(!0);assertEquals(0,container.getElement().scrollTop)}function testHighlightSecondLastScrollsNearTheBottom(){scroller=new goog.ui.ContainerScroller(container);container.getChildAt(8).setHighlighted(!0);assertEquals("Since scrolling is lazy, when highlighting the second"+" last, the item should be the last visible one.",80,container.getElement().scrollTop)}function testHighlightLastScrollsToBottom(){scroller=new goog.ui.ContainerScroller(container);container.getChildAt(9).setHighlighted(!0);assertEquals(100,container.getElement().scrollTop)}function testScrollRestoreIfStillVisible(){scroller=new goog.ui.ContainerScroller(container);container.getChildAt(9).setHighlighted(!0);var scrollTop=container.getElement().scrollTop;container.setVisible(!1);container.setVisible(!0);assertEquals("Scroll position should be the same after restore, if it "+"still makes highlighted item visible",scrollTop,container.getElement().scrollTop)}function testNoScrollRestoreIfNotVisible(){scroller=new goog.ui.ContainerScroller(container);container.getElement().scrollTop=100;container.setVisible(!1);container.getChildAt(0).setHighlighted(!0);container.setVisible(!0);assertNotEquals("Scroll position should not be the same after restore, if "+"the scroll position when the menu was hidden no longer "+"makes the highlighted item visible when the container is "+"shown again",100,container.getElement().scrollTop)}function testCenterOnHighlightedOnFirstOpen(){container.setVisible(!1);scroller=new goog.ui.ContainerScroller(container);container.getChildAt(4).setHighlighted(!0);container.setVisible(!0);assertEquals("On the very first display of the scroller, the item should be "+"centered, rather than just assured in view.",40,container.getElement().scrollTop)}function testHighlightsAreIgnoredInResponseToScrolling(){scroller=new goog.ui.ContainerScroller(container);container.getChildAt(9).setHighlighted(!0);goog.testing.events.fireMouseOverEvent(goog.dom.getElement("control-5"),goog.dom.getElement("control-9"));assertEquals("Mouseovers due to scrolls should be ignored",9,container.getHighlightedIndex())}function testHighlightsAreNotIgnoredWhenNotScrolling(){scroller=new goog.ui.ContainerScroller(container);container.getChildAt(5).setHighlighted(!0);mockClock.tick(1e3);goog.testing.events.fireMouseOutEvent(goog.dom.getElement("control-5"),goog.dom.getElement("control-6"));goog.testing.events.fireMouseOverEvent(goog.dom.getElement("control-6"),goog.dom.getElement("control-5"));assertEquals("Mousovers not due to scrolls should not be ignored",6,container.getHighlightedIndex())}function testFastSynchronousHighlightsNotIgnored(){scroller=new goog.ui.ContainerScroller(container);container.getChildAt(9).setHighlighted(!0);container.getChildAt(1).setHighlighted(!0);assertEquals("Synchronous highlights should NOT be ignored.",1,container.getHighlightedIndex());container.getChildAt(8).setHighlighted(!0);assertEquals("Synchronous highlights should NOT be ignored.",8,container.getHighlightedIndex())}function testInitialItemIsCentered(){container.getChildAt(4).setHighlighted(!0);scroller=new goog.ui.ContainerScroller(container);assertEquals("On the very first attachment of the scroller, the item should be "+"centered, rather than just assured in view.",40,container.getElement().scrollTop)}function testInitialItemIsCenteredTopItem(){container.getChildAt(0).setHighlighted(!0);scroller=new goog.ui.ContainerScroller(container);assertEquals(0,container.getElement().scrollTop)}function testHidingMenuItemsDoesntAffectContainerScroller(){scroller=new goog.ui.ContainerScroller(container);container.getElement=function(){fail("getElement() must not be called when a control in the container is "+"being hidden")};container.getChildAt(0).setVisible(!1)}