goog.provide("goog.ui.decorateTest");goog.setTestOnly("goog.ui.decorateTest");goog.require("goog.testing.jsunit");goog.require("goog.ui.decorate");goog.require("goog.ui.registry");function UnknownComponent(){}function FakeComponentX(){this.element=null}FakeComponentX.prototype.decorate=function(element){this.element=element};function FakeComponentY(){FakeComponentX.call(this)}goog.inherits(FakeComponentY,FakeComponentX);function FakeComponentZ(){}function FakeRenderer(){}function FakeSingletonRenderer(){}FakeSingletonRenderer.instance_=new FakeSingletonRenderer;FakeSingletonRenderer.getInstance=function(){return FakeSingletonRenderer.instance_};function setUp(){goog.ui.registry.setDefaultRenderer(FakeComponentX,FakeRenderer);goog.ui.registry.setDefaultRenderer(FakeComponentZ,FakeSingletonRenderer);goog.ui.registry.setDecoratorByClassName("fake-component-x",function(){return new FakeComponentX});goog.ui.registry.setDecoratorByClassName("fake-component-y",function(){return new FakeComponentY})}function tearDown(){goog.ui.registry.reset()}function testDecorate(){var dx=goog.ui.decorate(document.getElementById("x"));assertTrue("Decorator for element with fake-component-x class must be "+"a FakeComponentX",dx instanceof FakeComponentX);assertEquals("Element x must have been decorated",document.getElementById("x"),dx.element);var dy=goog.ui.decorate(document.getElementById("y"));assertTrue("Decorator for element with fake-component-y class must be "+"a FakeComponentY",dy instanceof FakeComponentY);assertEquals("Element y must have been decorated",document.getElementById("y"),dy.element);var dz=goog.ui.decorate(document.getElementById("z"));assertNull("Decorator for element with unknown class must be null",dz);var du=goog.ui.decorate(document.getElementById("u"));assertNull("Decorator for element without CSS class must be null",du)}