goog.provide("goog.ui.DimensionPickerRendererTest");goog.setTestOnly("goog.ui.DimensionPickerRendererTest");goog.require("goog.a11y.aria.LivePriority");goog.require("goog.array");goog.require("goog.testing.jsunit");goog.require("goog.testing.recordFunction");goog.require("goog.ui.DimensionPicker");goog.require("goog.ui.DimensionPickerRenderer");var renderer,picker;function setUp(){renderer=new goog.ui.DimensionPickerRenderer;picker=new goog.ui.DimensionPicker(renderer)}function tearDown(){picker.dispose()}function testSetHighlightedSizeUpdatesLiveRegion(){picker.render();var sayFunction=goog.testing.recordFunction();renderer.announcer_.say=sayFunction;renderer.setHighlightedSize(picker,3,7);assertEquals(1,sayFunction.getCallCount());assertTrue(goog.array.equals(["3 by 7",goog.a11y.aria.LivePriority.ASSERTIVE],sayFunction.getLastCall().getArguments()))}