goog.provide("goog.ui.DrilldownRowTest");goog.setTestOnly("goog.ui.DrilldownRowTest");goog.require("goog.dom");goog.require("goog.dom.TagName");goog.require("goog.html.SafeHtml");goog.require("goog.testing.jsunit");goog.require("goog.ui.DrilldownRow");function testMakeRows(){var ff=goog.dom.getElement("firstRow"),d=new goog.ui.DrilldownRow({}),d1=new goog.ui.DrilldownRow({html:createHtmlForRow("Second row")}),d2=new goog.ui.DrilldownRow({html:createHtmlForRow("Third row")}),d21=new goog.ui.DrilldownRow({html:createHtmlForRow("Fourth row")}),d22=new goog.ui.DrilldownRow(goog.ui.DrilldownRow.sampleProperties);d.decorate(ff);d.addChild(d1,!0);d.addChild(d2,!0);d2.addChild(d21,!0);d2.addChild(d22,!0);assertThrows(function(){d.findIndex()});assertEquals(0,d1.findIndex());assertEquals(1,d2.findIndex())}function createHtmlForRow(rowText){var SafeHtml=goog.html.SafeHtml;return SafeHtml.create(goog.dom.TagName.TR,{},SafeHtml.concat(goog.html.SafeHtml.create(goog.dom.TagName.TD,{},rowText),goog.html.SafeHtml.create(goog.dom.TagName.TD,{},"Second column")))}