goog.provide("goog.ui.editor.BubbleTest");goog.setTestOnly("goog.ui.editor.BubbleTest");goog.require("goog.dom");goog.require("goog.dom.TagName");goog.require("goog.events");goog.require("goog.events.EventType");goog.require("goog.positioning.Corner");goog.require("goog.positioning.OverflowStatus");goog.require("goog.string");goog.require("goog.style");goog.require("goog.testing.editor.TestHelper");goog.require("goog.testing.events");goog.require("goog.testing.jsunit");goog.require("goog.ui.Component");goog.require("goog.ui.editor.Bubble");goog.require("goog.userAgent.product");var testHelper,fieldDiv,bubble,link,link2,panelId;function setUpPage(){fieldDiv=goog.dom.getElement("field");var viewportSize=goog.dom.getViewportSize();if(600>viewportSize.width||440>viewportSize.height){window.moveTo(0,0);window.resizeTo(640,480)}}function setUp(){testHelper=new goog.testing.editor.TestHelper(fieldDiv);testHelper.setUpEditableElement();bubble=new goog.ui.editor.Bubble(document.body,999);fieldDiv.innerHTML="<a href=\"http://www.google.com\">Google</a>"+"<a href=\"http://www.google.com\">Google2</a>";link=fieldDiv.firstChild;link2=fieldDiv.lastChild;window.scrollTo(0,0);goog.style.setStyle(document.body,"direction","ltr");goog.style.setStyle(document.getElementById("field"),"position","static")}function tearDown(){if(panelId){bubble.removePanel(panelId);panelId=null}testHelper.tearDownEditableElement()}function prepareTargetWithGivenDirection(dir,opt_preferTopPosition){goog.style.setStyle(document.body,"direction",dir);fieldDiv.style.direction=dir;fieldDiv.innerHTML="<a href=\"http://www.google.com\">Google</a>";link=fieldDiv.firstChild;panelId=bubble.addPanel("A","Link",link,function(el){el.innerHTML="<div style=\"border:1px solid blue;\">B</div>"},opt_preferTopPosition)}function getExpectedBubblePositionWithGivenAlignment(opt_alignRight){var targetPosition=goog.style.getFramedPageOffset(link,window),targetWidth=link.offsetWidth,bubbleSize=goog.style.getSize(bubble.bubbleContainer_),expectedBubbleX=opt_alignRight?targetPosition.x+targetWidth-bubbleSize.width:targetPosition.x,expectedBubbleY=link.offsetHeight+targetPosition.y+goog.ui.editor.Bubble.VERTICAL_CLEARANCE_;return{x:expectedBubbleX,y:expectedBubbleY}}function testCreateBubbleWithLinkPanel(){var id=goog.string.createUniqueString();panelId=bubble.addPanel("A","Link",link,function(container){container.innerHTML="<span id=\""+id+"\">Test</span>"});assertNotNull("Bubble should be created",bubble.bubbleContents_);assertNotNull("Added element should be present",goog.dom.getElement(id));assertTrue("Bubble should be visible",bubble.isVisible())}function testCloseBubble(){testCreateBubbleWithLinkPanel();var count=0;goog.events.listen(bubble,goog.ui.Component.EventType.HIDE,function(){count++});bubble.removePanel(panelId);panelId=null;assertFalse("Bubble should not be visible",bubble.isVisible());assertEquals("Hide event should be dispatched",1,count)}function testCloseBox(){testCreateBubbleWithLinkPanel();var count=0;goog.events.listen(bubble,goog.ui.Component.EventType.HIDE,function(){count++});var closeBox=goog.dom.getElementsByTagNameAndClass(goog.dom.TagName.DIV,"tr_bubble_closebox",bubble.bubbleContainer_)[0];goog.testing.events.fireClickSequence(closeBox);panelId=null;assertFalse("Bubble should not be visible",bubble.isVisible());assertEquals("Hide event should be dispatched",1,count)}function testViewPortSizeMonitorEvent(){testCreateBubbleWithLinkPanel();var numCalled=0;bubble.reposition=function(){numCalled++};assertNotUndefined("viewPortSizeMonitor_ should not be undefined",bubble.viewPortSizeMonitor_);bubble.viewPortSizeMonitor_.dispatchEvent(goog.events.EventType.RESIZE);assertEquals("reposition not called",1,numCalled)}function testBubblePositionPreferTop(){called=!1;bubble.positionAtAnchor_=function(targetCorner,bubbleCorner,overflow){called=!0;assertEquals(goog.positioning.Corner.TOP_START,targetCorner);assertEquals(goog.positioning.Corner.BOTTOM_START,bubbleCorner);return goog.positioning.OverflowStatus.NONE};prepareTargetWithGivenDirection("ltr",!0);assertTrue(called)}function testBubblePosition(){panelId=bubble.addPanel("A","Link",link,goog.nullFunction);var CLEARANCE=goog.ui.editor.Bubble.VERTICAL_CLEARANCE_,bubbleContainer=bubble.bubbleContainer_,targetPos=goog.style.getFramedPageOffset(link,window),targetSize=goog.style.getSize(link),pos=goog.style.getFramedPageOffset(bubbleContainer);assertEquals(targetPos.y+targetSize.height+CLEARANCE,pos.y);assertEquals(targetPos.x,pos.x);var field=document.getElementById("field"),fieldPos=goog.style.getFramedPageOffset(field,window);fieldPos.y+=bubble.dom_.getViewportSize().height-(targetPos.y+targetSize.height);goog.style.setStyle(field,"position","absolute");goog.style.setPosition(field,fieldPos);bubble.reposition();var bubbleSize=goog.style.getSize(bubbleContainer);targetPosition=goog.style.getFramedPageOffset(link,window);pos=goog.style.getFramedPageOffset(bubbleContainer);assertEquals(targetPosition.y-CLEARANCE-bubbleSize.height,pos.y)}function testBubblePositionRightAligned(){if(goog.userAgent.product.SAFARI){return}prepareTargetWithGivenDirection("rtl");var expectedPos=getExpectedBubblePositionWithGivenAlignment(!0),pos=goog.style.getFramedPageOffset(bubble.bubbleContainer_);assertRoughlyEquals(expectedPos.x,pos.x,.1);assertRoughlyEquals(expectedPos.y,pos.y,.1)}function testBubblePositionLeftToRight(){if(goog.userAgent.product.SAFARI){return}goog.style.setStyle(bubble.bubbleContainer_,"direction","ltr");prepareTargetWithGivenDirection("rtl");var expectedPos=getExpectedBubblePositionWithGivenAlignment(),pos=goog.style.getFramedPageOffset(bubble.bubbleContainer_);assertRoughlyEquals(expectedPos.x,pos.x,.1);assertRoughlyEquals(expectedPos.y,pos.y,.1)}function testBubblePositionRightToLeft(){goog.style.setStyle(bubble.bubbleContainer_,"direction","rtl");prepareTargetWithGivenDirection("ltr");var expectedPos=getExpectedBubblePositionWithGivenAlignment(!0),pos=goog.style.getFramedPageOffset(bubble.bubbleContainer_);assertEquals(expectedPos.x,pos.x);assertEquals(expectedPos.y,pos.y)}