goog.provide("goog.ui.emoji.ProgressiveEmojiPaletteRenderer");goog.require("goog.dom.TagName");goog.require("goog.style");goog.require("goog.ui.emoji.EmojiPaletteRenderer");goog.ui.emoji.ProgressiveEmojiPaletteRenderer=function(defaultImgUrl){goog.ui.emoji.EmojiPaletteRenderer.call(this,defaultImgUrl)};goog.inherits(goog.ui.emoji.ProgressiveEmojiPaletteRenderer,goog.ui.emoji.EmojiPaletteRenderer);goog.ui.emoji.ProgressiveEmojiPaletteRenderer.prototype.buildElementFromSpriteMetadata=function(dom,spriteInfo,displayUrl){var width=spriteInfo.getWidthCssValue(),height=spriteInfo.getHeightCssValue(),x=spriteInfo.getXOffsetCssValue(),y=spriteInfo.getYOffsetCssValue(),inner=dom.createDom(goog.dom.TagName.IMG,{src:displayUrl}),el=dom.createDom(goog.dom.TagName.DIV,goog.getCssName("goog-palette-cell-extra"),inner);goog.style.setStyle(el,{width:width,height:height,overflow:"hidden",position:"relative"});goog.style.setStyle(inner,{left:x,top:y,position:"absolute"});return el};goog.ui.emoji.ProgressiveEmojiPaletteRenderer.prototype.updateAnimatedPaletteItem=function(item,animatedImg){var img,el=item.firstChild;while(el){if("IMG"==el.tagName){img=el;break}el=el.firstChild}if(!el){return}img.width=animatedImg.width;img.height=animatedImg.height;goog.style.setStyle(img,{left:0,top:0});img.src=animatedImg.src};