goog.provide("goog.ui.FlatButtonRenderer");goog.require("goog.a11y.aria.Role");goog.require("goog.asserts");goog.require("goog.dom.TagName");goog.require("goog.dom.classlist");goog.require("goog.ui.Button");goog.require("goog.ui.ButtonRenderer");goog.require("goog.ui.INLINE_BLOCK_CLASSNAME");goog.require("goog.ui.registry");goog.ui.FlatButtonRenderer=function(){goog.ui.ButtonRenderer.call(this)};goog.inherits(goog.ui.FlatButtonRenderer,goog.ui.ButtonRenderer);goog.addSingletonGetter(goog.ui.FlatButtonRenderer);goog.ui.FlatButtonRenderer.CSS_CLASS=goog.getCssName("goog-flat-button");goog.ui.FlatButtonRenderer.prototype.createDom=function(button){var classNames=this.getClassNames(button),attributes={class:goog.ui.INLINE_BLOCK_CLASSNAME+" "+classNames.join(" ")},element=button.getDomHelper().createDom(goog.dom.TagName.DIV,attributes,button.getContent());this.setTooltip(element,button.getTooltip());return element};goog.ui.FlatButtonRenderer.prototype.getAriaRole=function(){return goog.a11y.aria.Role.BUTTON};goog.ui.FlatButtonRenderer.prototype.canDecorate=function(element){return element.tagName==goog.dom.TagName.DIV};goog.ui.FlatButtonRenderer.prototype.decorate=function(button,element){goog.asserts.assert(element);goog.dom.classlist.add(element,goog.ui.INLINE_BLOCK_CLASSNAME);return goog.ui.FlatButtonRenderer.superClass_.decorate.call(this,button,element)};goog.ui.FlatButtonRenderer.prototype.getValue=function(element){return""};goog.ui.FlatButtonRenderer.prototype.getCssClass=function(){return goog.ui.FlatButtonRenderer.CSS_CLASS};goog.ui.registry.setDecoratorByClassName(goog.ui.FlatButtonRenderer.CSS_CLASS,function(){return new goog.ui.Button(null,goog.ui.FlatButtonRenderer.getInstance())});