goog.provide("goog.ui.FormPost");goog.require("goog.array");goog.require("goog.dom.InputType");goog.require("goog.dom.TagName");goog.require("goog.dom.safe");goog.require("goog.html.SafeHtml");goog.require("goog.ui.Component");goog.ui.FormPost=function(opt_dom){goog.ui.Component.call(this,opt_dom)};goog.inherits(goog.ui.FormPost,goog.ui.Component);goog.ui.FormPost.prototype.createDom=function(){this.setElementInternal(this.getDomHelper().createDom(goog.dom.TagName.FORM,{method:"POST",style:"display:none"}))};goog.ui.FormPost.prototype.post=function(parameters,opt_url,opt_target){var form=this.getElement();if(!form){this.render();form=this.getElement()}form.action=opt_url||"";form.target=opt_target||"";this.setParameters_(form,parameters);form.submit()};goog.ui.FormPost.prototype.setParameters_=function(form,parameters){var name,value,html=[];for(name in parameters){value=parameters[name];if(goog.isArrayLike(value)){goog.array.forEach(value,goog.bind(function(innerValue){html.push(this.createInput_(name,innerValue+""))},this))}else{html.push(this.createInput_(name,value+""))}}goog.dom.safe.setInnerHtml(form,goog.html.SafeHtml.concat(html))};goog.ui.FormPost.prototype.createInput_=function(name,value){return goog.html.SafeHtml.create("input",{type:goog.dom.InputType.HIDDEN,name:name,value:value})};