goog.provide("goog.ui.InputDatePickerTest");goog.setTestOnly("goog.ui.InputDatePickerTest");goog.require("goog.dom");goog.require("goog.i18n.DateTimeFormat");goog.require("goog.i18n.DateTimeParse");goog.require("goog.testing.jsunit");goog.require("goog.ui.InputDatePicker");var dateTimeFormatter=new goog.i18n.DateTimeFormat("MM/dd/yyyy"),dateTimeParser=new goog.i18n.DateTimeParse("MM/dd/yyyy"),inputDatePicker,popupDatePicker;function setUp(){}function tearDown(){if(inputDatePicker){inputDatePicker.dispose()}if(popupDatePicker){popupDatePicker.dispose()}goog.dom.removeChildren(goog.dom.getElement("renderElement"));goog.dom.removeChildren(goog.dom.getElement("popupParent"))}function test_setPopupParentElementDefault(){setPopupParentElement_(null);assertEquals("PopupDatePicker should be parented to the body element",document.body,popupDatePicker.getElement().parentNode)}function test_setPopupParentElement(){var popupParentElement=goog.dom.getElement("popupParent");setPopupParentElement_(popupParentElement);assertEquals("PopupDatePicker should be parented to the popupParent DIV",popupParentElement,popupDatePicker.getElement().parentNode)}function setPopupParentElement_(el){inputDatePicker=new goog.ui.InputDatePicker(dateTimeFormatter,dateTimeParser);if(el){inputDatePicker.setPopupParentElement(el)}inputDatePicker.render(goog.dom.getElement("renderElement"));popupDatePicker=inputDatePicker.popupDatePicker_}function test_ItParsesDataCorrectly(){inputDatePicker=new goog.ui.InputDatePicker(dateTimeFormatter,dateTimeParser);inputDatePicker.render(goog.dom.getElement("renderElement"));inputDatePicker.createDom();inputDatePicker.setInputValue("8/9/2009");var parsedDate=inputDatePicker.getInputValueAsDate_();assertEquals(2009,parsedDate.getYear());assertEquals(7,parsedDate.getMonth());assertEquals(9,parsedDate.getDate())}function test_ItUpdatesItsValueOnPopupShown(){inputDatePicker=new goog.ui.InputDatePicker(dateTimeFormatter,dateTimeParser);setPopupParentElement_(null);inputDatePicker.setInputValue("1/1/1");inputDatePicker.showForElement(document.body);var inputValue=inputDatePicker.getInputValue();assertEquals("01/01/0001",inputValue)}function test_ItDoesNotClearInputOnPopupShown(){inputDatePicker=new goog.ui.InputDatePicker(dateTimeFormatter,dateTimeParser);setPopupParentElement_(null);inputDatePicker.setInputValue("i_am_not_a_date");inputDatePicker.showForElement(document.body);var inputValue=inputDatePicker.getInputValue();assertEquals("i_am_not_a_date",inputValue)}