goog.provide("goog.ui.KeyboardShortcutHandlerTest");goog.setTestOnly("goog.ui.KeyboardShortcutHandlerTest");goog.require("goog.dom");goog.require("goog.events");goog.require("goog.events.BrowserEvent");goog.require("goog.events.KeyCodes");goog.require("goog.testing.MockClock");goog.require("goog.testing.PropertyReplacer");goog.require("goog.testing.StrictMock");goog.require("goog.testing.events");goog.require("goog.testing.jsunit");goog.require("goog.ui.KeyboardShortcutHandler");goog.require("goog.userAgent");var Modifiers=goog.ui.KeyboardShortcutHandler.Modifiers,KeyCodes=goog.events.KeyCodes,handler,targetDiv,listener,mockClock,stubs=new goog.testing.PropertyReplacer;function fire(keycode,opt_extraProperties,opt_element){return goog.testing.events.fireKeySequence(opt_element||targetDiv,keycode,opt_extraProperties)}function fireAltGraphKey(keycode,keyPressKeyCode,opt_extraProperties,opt_element){return goog.testing.events.fireNonAsciiKeySequence(opt_element||targetDiv,keycode,keyPressKeyCode,opt_extraProperties)}function setUp(){targetDiv=goog.dom.getElement("targetDiv");handler=new goog.ui.KeyboardShortcutHandler(goog.dom.getElement("rootDiv"));listener=new goog.testing.StrictMock({shortcutFired:goog.nullFunction});goog.events.listen(handler,goog.ui.KeyboardShortcutHandler.EventType.SHORTCUT_TRIGGERED,function(event){listener.shortcutFired(event.identifier)});mockClock=new goog.testing.MockClock(!0)}function tearDown(){mockClock.uninstall();handler.dispose();stubs.reset()}function testAllowsSingleLetterKeyBindingsSpecifiedAsString(){listener.shortcutFired("lettergee");listener.$replay();handler.registerShortcut("lettergee","g");fire(KeyCodes.G);listener.$verify()}function testAllowsSingleLetterKeyBindingsSpecifiedAsKeyCode(){listener.shortcutFired("lettergee");listener.$replay();handler.registerShortcut("lettergee",KeyCodes.G);fire(KeyCodes.G);listener.$verify()}function testDoesntFireWhenWrongKeyIsPressed(){listener.$replay();handler.registerShortcut("letterjay","j");fire(KeyCodes.G);listener.$verify()}function testAllowsControlAndLetterSpecifiedAsAString(){listener.shortcutFired("lettergee");listener.$replay();handler.registerShortcut("lettergee","ctrl+g");fire(KeyCodes.G,{ctrlKey:!0});listener.$verify()}function testAllowsControlAndLetterSpecifiedAsArgSequence(){listener.shortcutFired("lettergeectrl");listener.$replay();handler.registerShortcut("lettergeectrl",KeyCodes.G,Modifiers.CTRL);fire(KeyCodes.G,{ctrlKey:!0});listener.$verify()}function testAllowsControlAndLetterSpecifiedAsArray(){listener.shortcutFired("lettergeectrl");listener.$replay();handler.registerShortcut("lettergeectrl",[KeyCodes.G,Modifiers.CTRL]);fire(KeyCodes.G,{ctrlKey:!0});listener.$verify()}function testAllowsShift(){listener.shortcutFired("lettergeeshift");listener.$replay();handler.registerShortcut("lettergeeshift",[KeyCodes.G,Modifiers.SHIFT]);fire(KeyCodes.G,{shiftKey:!0});listener.$verify()}function testAllowsAlt(){listener.shortcutFired("lettergeealt");listener.$replay();handler.registerShortcut("lettergeealt",[KeyCodes.G,Modifiers.ALT]);fire(KeyCodes.G,{altKey:!0});listener.$verify()}function testAllowsMeta(){listener.shortcutFired("lettergeemeta");listener.$replay();handler.registerShortcut("lettergeemeta",[KeyCodes.G,Modifiers.META]);fire(KeyCodes.G,{metaKey:!0});listener.$verify()}function testAllowsMultipleModifiers(){listener.shortcutFired("lettergeectrlaltshift");listener.$replay();handler.registerShortcut("lettergeectrlaltshift",KeyCodes.G,Modifiers.CTRL|Modifiers.ALT|Modifiers.SHIFT);fireAltGraphKey(KeyCodes.G,0,{ctrlKey:!0,altKey:!0,shiftKey:!0});listener.$verify()}function testAllowsMultipleModifiersSpecifiedAsString(){listener.shortcutFired("lettergeectrlaltshiftmeta");listener.$replay();handler.registerShortcut("lettergeectrlaltshiftmeta","ctrl+shift+alt+meta+g");fireAltGraphKey(KeyCodes.G,0,{ctrlKey:!0,altKey:!0,shiftKey:!0,metaKey:!0});listener.$verify()}function testPreventsDefaultOnReturnFalse(){listener.shortcutFired("x");listener.$replay();handler.registerShortcut("x","x");var key=goog.events.listen(handler,goog.ui.KeyboardShortcutHandler.EventType.SHORTCUT_TRIGGERED,function(event){return!1});assertFalse("return false in listener must prevent default",fire(KeyCodes.X));listener.$verify();goog.events.unlistenByKey(key)}function testPreventsDefaultWhenExceptionThrown(){handler.registerShortcut("x","x");handler.setAlwaysPreventDefault(!0);goog.events.listenOnce(handler,goog.ui.KeyboardShortcutHandler.EventType.SHORTCUT_TRIGGERED,function(event){throw new Error("x")});var callCount=0;stubs.set(goog.events.BrowserEvent.prototype,"preventDefault",function(){callCount++});var e=assertThrows(goog.partial(fire,KeyCodes.X));assertEquals("x",e.message);assertEquals(1,callCount)}function testDoesntFireWhenUserForgetsRequiredModifier(){listener.$replay();handler.registerShortcut("lettergeectrl",KeyCodes.G,Modifiers.CTRL);fire(KeyCodes.G);listener.$verify()}function testDoesntFireIfTooManyModifiersPressed(){listener.$replay();handler.registerShortcut("lettergeectrl",KeyCodes.G,Modifiers.CTRL);fire(KeyCodes.G,{ctrlKey:!0,metaKey:!0});listener.$verify()}function testDoesntFireIfAnyRequiredModifierForgotten(){listener.$replay();handler.registerShortcut("lettergeectrlaltshift",KeyCodes.G,Modifiers.CTRL|Modifiers.ALT|Modifiers.SHIFT);fire(KeyCodes.G,{altKey:!0,shiftKey:!0});listener.$verify()}function testAllowsMultiKeySequenceSpecifiedAsArray(){listener.shortcutFired("quitemacs");listener.$replay();handler.registerShortcut("quitemacs",[KeyCodes.X,Modifiers.CTRL,KeyCodes.C]);assertFalse(fire(KeyCodes.X,{ctrlKey:!0}));fire(KeyCodes.C);listener.$verify()}function testAllowsMultiKeySequenceSpecifiedAsArguments(){listener.shortcutFired("quitvi");listener.$replay();handler.registerShortcut("quitvi",KeyCodes.SEMICOLON,Modifiers.SHIFT,KeyCodes.Q,Modifiers.NONE,KeyCodes.NUM_ONE,Modifiers.SHIFT);var shiftProperties={shiftKey:!0};assertFalse(fire(KeyCodes.SEMICOLON,shiftProperties));assertFalse(fire(KeyCodes.Q));fire(KeyCodes.NUM_ONE,shiftProperties);listener.$verify()}function testMultiKeyEventIsNotFiredIfUserIsTooSlow(){listener.$replay();handler.registerShortcut("quitemacs",[KeyCodes.X,Modifiers.CTRL,KeyCodes.C]);fire(KeyCodes.X,{ctrlKey:!0});mockClock.tick(3e3);fire(KeyCodes.C);listener.$verify()}function testAllowsMultipleAHandlers(){listener.shortcutFired("quitvi");listener.shortcutFired("letterex");listener.shortcutFired("quitemacs");listener.$replay();handler.registerShortcut("quitvi",KeyCodes.SEMICOLON,Modifiers.SHIFT,KeyCodes.Q,Modifiers.NONE,KeyCodes.NUM_ONE,Modifiers.SHIFT);handler.registerShortcut("quitemacs",[KeyCodes.X,Modifiers.CTRL,KeyCodes.C]);handler.registerShortcut("letterex","x");var shiftProperties={shiftKey:!0};fire(KeyCodes.SEMICOLON,shiftProperties);fire(KeyCodes.Q);fire(KeyCodes.NUM_ONE,shiftProperties);fire(KeyCodes.X);fire(KeyCodes.X,{ctrlKey:!0});fire(KeyCodes.C);listener.$verify()}function testCanRemoveOneHandler(){listener.shortcutFired("letterex");listener.$replay();handler.registerShortcut("quitvi",KeyCodes.COLON,Modifiers.NONE,KeyCodes.Q,Modifiers.NONE,KeyCodes.EXCLAMATION,Modifiers.NONE);handler.registerShortcut("letterex","x");handler.unregisterShortcut(KeyCodes.COLON,Modifiers.NONE,KeyCodes.Q,Modifiers.NONE,KeyCodes.EXCLAMATION,Modifiers.NONE);fire(KeyCodes.COLON);fire(KeyCodes.Q);fire(KeyCodes.EXCLAMATION);fire(KeyCodes.X);listener.$verify()}function testCanRemoveTwoHandlers(){listener.$replay();handler.registerShortcut("quitemacs",[KeyCodes.X,Modifiers.CTRL,KeyCodes.C]);handler.registerShortcut("letterex","x");handler.unregisterShortcut([KeyCodes.X,Modifiers.CTRL,KeyCodes.C]);handler.unregisterShortcut("x");fire(KeyCodes.X,{ctrlKey:!0});fire(KeyCodes.C);fire(KeyCodes.X);listener.$verify()}function testIsShortcutRegistered_single(){assertFalse(handler.isShortcutRegistered("x"));handler.registerShortcut("letterex","x");assertTrue(handler.isShortcutRegistered("x"));handler.unregisterShortcut("x");assertFalse(handler.isShortcutRegistered("x"))}function testIsShortcutRegistered_multi(){assertFalse(handler.isShortcutRegistered("a"));assertFalse(handler.isShortcutRegistered("a b"));assertFalse(handler.isShortcutRegistered("a b c"));handler.registerShortcut("ab","a b");assertFalse(handler.isShortcutRegistered("a"));assertTrue(handler.isShortcutRegistered("a b"));assertFalse(handler.isShortcutRegistered("a b c"));handler.unregisterShortcut("a b");assertFalse(handler.isShortcutRegistered("a"));assertFalse(handler.isShortcutRegistered("a b"));assertFalse(handler.isShortcutRegistered("a b c"))}function testUnregister_subsequence(){handler.registerShortcut("abc","a b c");handler.unregisterShortcut("a b");assertTrue(handler.isShortcutRegistered("a b c"))}function testUnregister_supersequence(){handler.registerShortcut("ab","a b");handler.unregisterShortcut("a b c");assertTrue(handler.isShortcutRegistered("a b"))}function testUnregister_partialMatchSequence(){handler.registerShortcut("abc","a b c");handler.unregisterShortcut("a b x");assertTrue(handler.isShortcutRegistered("a b c"))}function testUnregister_deadBranch(){handler.registerShortcut("abc","a b c");handler.unregisterShortcut("a b c");assertTrue(fire(KeyCodes.A))}function registerEnterSpaceXF1AltY(){handler.registerShortcut("enter",KeyCodes.ENTER);handler.registerShortcut("space",KeyCodes.SPACE);handler.registerShortcut("x","x");handler.registerShortcut("global",KeyCodes.F1);handler.registerShortcut("withAlt","alt+y")}function fireEnterSpaceXF1AltY(target){fire(KeyCodes.ENTER,void 0,target);fire(KeyCodes.SPACE,void 0,target);fire(KeyCodes.X,void 0,target);fire(KeyCodes.F1,void 0,target);fire(KeyCodes.Y,{altKey:!0},target)}function testIgnoreNonGlobalShortcutsInSelect(){var targetSelect=goog.dom.getElement("targetSelect");listener.shortcutFired("global");listener.shortcutFired("withAlt");listener.$replay();registerEnterSpaceXF1AltY();fireEnterSpaceXF1AltY(goog.dom.getElement("targetSelect"));listener.$verify()}function testIgnoreNonGlobalShortcutsInTextArea(){listener.shortcutFired("global");listener.shortcutFired("withAlt");listener.$replay();registerEnterSpaceXF1AltY();fireEnterSpaceXF1AltY(goog.dom.getElement("targetTextArea"));listener.$verify()}function expectShortcutsOnTargets(shortcuts,targets,fireEvents){for(var i=0,ii=targets.length;i<ii;i++){for(var j=0,jj=shortcuts.length;j<jj;j++){listener.shortcutFired(shortcuts[j])}listener.$replay();fireEvents(goog.dom.getElement(targets[i]));listener.$verify();listener.$reset()}}function testIgnoreShortcutsExceptEnterInTextInputFields(){var targets=["targetColor","targetDate","targetDateTime","targetDateTimeLocal","targetEmail","targetMonth","targetNumber","targetPassword","targetSearch","targetTel","targetText","targetTime","targetUrl","targetWeek"];registerEnterSpaceXF1AltY();expectShortcutsOnTargets(["enter","global","withAlt"],targets,fireEnterSpaceXF1AltY)}function testIgnoreSpaceInCheckBoxAndButton(){registerEnterSpaceXF1AltY();expectShortcutsOnTargets(["enter","x","global","withAlt"],["targetCheckBox","targetButton"],fireEnterSpaceXF1AltY)}function testIgnoreNonGlobalShortcutsInContentEditable(){var setDesignMode=!goog.userAgent.IE||!goog.userAgent.isVersionOrHigher("9");try{if(setDesignMode){document.designMode="on"}targetDiv.contentEditable="true";listener.shortcutFired("global");listener.$replay();registerEnterSpaceXF1AltY();fireEnterSpaceXF1AltY(targetDiv);listener.$verify()}finally{if(setDesignMode){document.designMode="off"}targetDiv.contentEditable="false"}}function testSetAllShortcutsAreGlobal(){handler.setAllShortcutsAreGlobal(!0);registerEnterSpaceXF1AltY();expectShortcutsOnTargets(["enter","space","x","global","withAlt"],["targetTextArea"],fireEnterSpaceXF1AltY)}function testSetModifierShortcutsAreGlobalFalse(){handler.setModifierShortcutsAreGlobal(!1);registerEnterSpaceXF1AltY();expectShortcutsOnTargets(["global"],["targetTextArea"],fireEnterSpaceXF1AltY)}function testAltGraphKeyOnUSLayout(){listener.shortcutFired("letterOne");listener.shortcutFired("letterTwo");listener.shortcutFired("letterThree");listener.shortcutFired("letterFour");listener.shortcutFired("letterFive");if(goog.userAgent.WINDOWS){listener.$replay();handler.registerShortcut("letterOne","ctrl+alt+1");handler.registerShortcut("letterTwo","ctrl+alt+2");handler.registerShortcut("letterThree","ctrl+alt+3");handler.registerShortcut("letterFour","ctrl+alt+4");handler.registerShortcut("letterFive","ctrl+alt+5");fireAltGraphKey(KeyCodes.ONE,0,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.TWO,0,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.THREE,0,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.FOUR,0,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.FIVE,0,{ctrlKey:!0,altKey:!0});listener.$verify()}}function testAltGraphKeyOnFrenchLayout(){listener.shortcutFired("letterOne");if(goog.userAgent.WINDOWS){listener.$replay();handler.registerShortcut("letterOne","ctrl+alt+1");handler.registerShortcut("letterTwo","ctrl+alt+2");handler.registerShortcut("letterThree","ctrl+alt+3");handler.registerShortcut("letterFour","ctrl+alt+4");handler.registerShortcut("letterFive","ctrl+alt+5");fireAltGraphKey(KeyCodes.ONE,0,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.TWO,771,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.THREE,35,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.FOUR,123,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.FIVE,8283,{ctrlKey:!0,altKey:!0});listener.$verify()}}function testAltGraphKeyOnSpanishLayout(){if(goog.userAgent.WINDOWS){listener.$replay();handler.registerShortcut("letterOne","ctrl+alt+1");handler.registerShortcut("letterTwo","ctrl+alt+2");handler.registerShortcut("letterThree","ctrl+alt+3");handler.registerShortcut("letterFour","ctrl+alt+4");handler.registerShortcut("letterFive","ctrl+alt+5");fireAltGraphKey(KeyCodes.ONE,124,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.TWO,64,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.THREE,35,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.FOUR,771,{ctrlKey:!0,altKey:!0});fireAltGraphKey(KeyCodes.FIVE,8364,{ctrlKey:!0,altKey:!0});listener.$verify()}}function testAltGraphKeyOnPolishLayout_withShift(){if(goog.userAgent.WINDOWS){listener.shortcutFired("letterQ");listener.$replay();handler.registerShortcut("letterA","ctrl+alt+shift+A");handler.registerShortcut("letterQ","ctrl+alt+shift+Q");assertTrue(fireAltGraphKey(KeyCodes.A,260,{ctrlKey:!0,altKey:!0,shiftKey:!0}));assertFalse(fireAltGraphKey(KeyCodes.Q,0,{ctrlKey:!0,altKey:!0,shiftKey:!0}));listener.$verify()}}function testNumpadKeyShortcuts(){for(var testCases=[["letterNumpad0","num-0",KeyCodes.NUM_ZERO],["letterNumpad1","num-1",KeyCodes.NUM_ONE],["letterNumpad2","num-2",KeyCodes.NUM_TWO],["letterNumpad3","num-3",KeyCodes.NUM_THREE],["letterNumpad4","num-4",KeyCodes.NUM_FOUR],["letterNumpad5","num-5",KeyCodes.NUM_FIVE],["letterNumpad6","num-6",KeyCodes.NUM_SIX],["letterNumpad7","num-7",KeyCodes.NUM_SEVEN],["letterNumpad8","num-8",KeyCodes.NUM_EIGHT],["letterNumpad9","num-9",KeyCodes.NUM_NINE],["letterNumpadMultiply","num-multiply",KeyCodes.NUM_MULTIPLY],["letterNumpadPlus","num-plus",KeyCodes.NUM_PLUS],["letterNumpadMinus","num-minus",KeyCodes.NUM_MINUS],["letterNumpadPERIOD","num-period",KeyCodes.NUM_PERIOD],["letterNumpadDIVISION","num-division",KeyCodes.NUM_DIVISION]],i=0;i<testCases.length;++i){listener.shortcutFired(testCases[i][0])}listener.$replay();for(var i=0;i<testCases.length;++i){handler.registerShortcut(testCases[i][0],testCases[i][1]);fire(testCases[i][2])}listener.$verify()}function testGeckoShortcuts(){listener.shortcutFired("1");listener.$replay();handler.registerShortcut("1","semicolon");if(goog.userAgent.GECKO){fire(goog.events.KeyCodes.FF_SEMICOLON)}else{fire(goog.events.KeyCodes.SEMICOLON)}listener.$verify()}function testWindows_multiKeyShortcuts(){if(goog.userAgent.WINDOWS){listener.shortcutFired("1");listener.$replay();handler.registerShortcut("1","ctrl+alt+n c");fire(KeyCodes.N,{ctrlKey:!0,altKey:!0});fire(KeyCodes.C);listener.$verify()}}function testWindows_multikeyShortcuts_polishKey(){if(goog.userAgent.WINDOWS){listener.$replay();handler.registerShortcut("announceCursorLocation","ctrl+alt+a l");assertTrue(fireAltGraphKey(KeyCodes.A,260,{ctrlKey:!0,altKey:!0}));listener.$verify()}}function testRegisterShortcut_modifierOnly(){assertThrows("Registering a shortcut with just modifiers should fail.",goog.bind(handler.registerShortcut,handler,"name","Shift"))}function testParseStringShortcut_unknownKey(){assertThrows("Unknown keys should fail.",goog.bind(goog.ui.KeyboardShortcutHandler.parseStringShortcut,null,"NotAKey"))}function testParseStringShortcut_resetKeyCode(){var strokes=goog.ui.KeyboardShortcutHandler.parseStringShortcut("A Shift");assertNull("The second stroke only has a modifier key.",strokes[1].keyCode)}