goog.provide("goog.ui.MockActivityMonitor");goog.require("goog.events.EventType");goog.require("goog.ui.ActivityMonitor");goog.ui.MockActivityMonitor=function(){goog.ui.MockActivityMonitor.base(this,"constructor");this.eventFired_=!1};goog.inherits(goog.ui.MockActivityMonitor,goog.ui.ActivityMonitor);goog.ui.MockActivityMonitor.prototype.simulateEvent=function(opt_type){var eventTime=goog.now(),eventType=opt_type||goog.events.EventType.MOUSEMOVE;this.eventFired_=!1;this.updateIdleTime(eventTime,eventType);if(!this.eventFired_){this.dispatchEvent(goog.ui.ActivityMonitor.Event.ACTIVITY)}};goog.ui.MockActivityMonitor.prototype.dispatchEvent=function(e){var rv=goog.ui.MockActivityMonitor.base(this,"dispatchEvent",e);this.eventFired_=!0;return rv};