goog.provide("goog.ui.PopupColorPickerTest");goog.setTestOnly("goog.ui.PopupColorPickerTest");goog.require("goog.dom");goog.require("goog.events");goog.require("goog.testing.events");goog.require("goog.testing.jsunit");goog.require("goog.ui.ColorPicker");goog.require("goog.ui.PopupColorPicker");function testPopupCreation(){var picker=new goog.ui.PopupColorPicker;picker.createDom();assertNotNull(picker.getPopup())}function testAutoHideIsSetProperly(){var picker=new goog.ui.PopupColorPicker;picker.createDom();picker.setAutoHide(!0);var containingDiv=goog.dom.getElement("containingDiv");picker.setAutoHideRegion(containingDiv);assertTrue(picker.getAutoHide());assertEquals(containingDiv,picker.getAutoHideRegion())}function testCustomColorPicker(){var button1=document.getElementById("button1"),domHelper=goog.dom.getDomHelper(),colorPicker=new goog.ui.ColorPicker;colorPicker.setColors(["#ffffff","#000000"]);var picker=new goog.ui.PopupColorPicker(domHelper,colorPicker);picker.render();picker.attach(button1);assertNotNull(picker.getColorPicker());assertNotNull(picker.getPopup().getElement());assertNull(picker.getSelectedColor());var changeEvents=0;goog.events.listen(picker,goog.ui.ColorPicker.EventType.CHANGE,function(e){changeEvents++});goog.testing.events.fireClickSequence(button1);goog.testing.events.fireClickSequence(document.getElementById("goog-palette-cell-0").firstChild);assertEquals("#ffffff",picker.getSelectedColor());assertEquals(1,changeEvents)}