goog.provide("goog.ui.PromptTest");goog.setTestOnly("goog.ui.PromptTest");goog.require("goog.dom.selection");goog.require("goog.events.InputHandler");goog.require("goog.events.KeyCodes");goog.require("goog.functions");goog.require("goog.string");goog.require("goog.testing.events");goog.require("goog.testing.jsunit");goog.require("goog.ui.BidiInput");goog.require("goog.ui.Dialog");goog.require("goog.ui.Prompt");goog.require("goog.userAgent");goog.require("goog.userAgent.product");var prompt;function setUp(){document.body.focus()}function tearDown(){goog.dispose(prompt)}function testFocusOnInputElement(){if(goog.userAgent.GECKO&&document.hasFocus&&!document.hasFocus()){return}var promptResult;prompt=new goog.ui.Prompt("title","Prompt:",function(result){promptResult=result},"defaultValue");prompt.setVisible(!0);if(goog.userAgent.product.CHROME){return}assertEquals("defaultValue",goog.dom.selection.getText(prompt.userInputEl_))}function testValidationFunction(){var promptResult;prompt=new goog.ui.Prompt("title","Prompt:",function(result){promptResult=result},"");prompt.setValidationFunction(goog.functions.not(goog.string.isEmptyOrWhitespace));prompt.setVisible(!0);var buttonSet=prompt.getButtonSet(),okButton=buttonSet.getButton(goog.ui.Dialog.DefaultButtonKeys.OK);assertTrue(okButton.disabled);prompt.userInputEl_.value="";goog.testing.events.fireKeySequence(prompt.userInputEl_,goog.events.KeyCodes.SPACE);assertTrue(okButton.disabled);prompt.userInputEl_.value="foo";goog.testing.events.fireKeySequence(prompt.userInputEl_,goog.events.KeyCodes.X);assertFalse(okButton.disabled)}function testBidiInput(){var shalomInHebrew="\u05E9\u05DC\u05D5\u05DD",promptResult=void 0;prompt=new goog.ui.Prompt("title","Prompt:",goog.functions.NULL,"");var bidiInput=new goog.ui.BidiInput;prompt.setInputDecoratorFn(goog.bind(bidiInput.decorate,bidiInput));prompt.setVisible(!0);prompt.userInputEl_.value=shalomInHebrew;goog.testing.events.fireKeySequence(prompt.userInputEl_,goog.events.KeyCodes.SPACE);goog.testing.events.fireBrowserEvent({target:prompt.userInputEl_,type:"input"});bidiInput.inputHandler_.dispatchEvent(goog.events.InputHandler.EventType.INPUT);assertEquals("rtl",prompt.userInputEl_.dir);prompt.userInputEl_.value="shalomInEnglish";goog.testing.events.fireKeySequence(prompt.userInputEl_,goog.events.KeyCodes.SPACE);goog.testing.events.fireBrowserEvent({target:prompt.userInputEl_,type:"input"});bidiInput.inputHandler_.dispatchEvent(goog.events.InputHandler.EventType.INPUT);assertEquals("ltr",prompt.userInputEl_.dir);goog.dispose(bidiInput)}function testBidiInput_off(){var shalomInHebrew="\u05E9\u05DC\u05D5\u05DD",promptResult=void 0;prompt=new goog.ui.Prompt("title","Prompt:",goog.functions.NULL,"");prompt.setVisible(!0);prompt.userInputEl_.value=shalomInHebrew;goog.testing.events.fireKeySequence(prompt.userInputEl_,goog.events.KeyCodes.SPACE);goog.testing.events.fireBrowserEvent({target:prompt.userInputEl_,type:"input"});assertEquals("",prompt.userInputEl_.dir);prompt.userInputEl_.value="shalomInEnglish";goog.testing.events.fireKeySequence(prompt.userInputEl_,goog.events.KeyCodes.SPACE);assertEquals("",prompt.userInputEl_.dir)}function newPrompt(){prompt=new goog.ui.Prompt("title","Prompt:",function(result){alert("Result: "+result);goog.dispose(prompt)},"defaultValue");prompt.setVisible(!0)}