goog.provide("goog.ui.RangeModelTest");goog.setTestOnly("goog.ui.RangeModelTest");goog.require("goog.testing.jsunit");goog.require("goog.ui.RangeModel");function reset(rm,step){rm.setStep(step||1);rm.setExtent(0);rm.setMinimum(0);rm.setMaximum(100);rm.setValue(0)}function getDescriptiveString(rm){return rm.getMinimum()+" < "+rm.getValue()+"["+rm.getExtent()+"] < "+rm.getMaximum()}function testValue(){var rm=new goog.ui.RangeModel;assertEquals(0,rm.getValue());rm.setValue(50);assertEquals(50,rm.getValue());rm.setValue(-1);assertEquals(0,rm.getValue());rm.setValue(101);assertEquals(100,rm.getValue());rm.setValue(0);rm.setExtent(10);rm.setValue(100);assertEquals(90,rm.getValue());reset(rm,3);assertEquals(0,rm.getValue());rm.setValue(50);assertEquals(51,rm.getValue());rm.setValue(-1);assertEquals(0,rm.getValue());rm.setValue(101);assertEquals(99,rm.getValue());rm.setValue(0);rm.setExtent(10);rm.setValue(100);assertEquals(90,rm.getValue())}function testMinium(){var rm=new goog.ui.RangeModel;rm.setValue(50);rm.setMinimum(10);assertEquals(10,rm.getMinimum());reset(rm);rm.setMinimum(10);assertEquals(10,rm.getMinimum());assertEquals(10,rm.getValue());rm.setMinimum(200);assertEquals(200,rm.getMinimum());assertEquals(200,rm.getValue());assertEquals(200,rm.getMaximum());assertEquals(0,rm.getExtent());reset(rm);rm.setExtent(10);rm.setMinimum(95);assertEquals(95,rm.getMinimum());assertEquals(95,rm.getValue());assertEquals(100,rm.getMaximum());assertEquals(5,rm.getExtent());reset(rm,3);rm.setValue(50);rm.setMinimum(10);assertEquals(10,rm.getMinimum());reset(rm,3);rm.setMinimum(10);assertEquals(10,rm.getMinimum());assertEquals(10,rm.getValue());rm.setMinimum(200);assertEquals(200,rm.getMinimum());assertEquals(200,rm.getValue());assertEquals(200,rm.getMaximum());assertEquals(0,rm.getExtent());reset(rm,3);rm.setExtent(10);rm.setMinimum(95);assertEquals(95,rm.getMinimum());assertEquals(95,rm.getValue());assertEquals(98,rm.getMaximum());assertEquals(3,rm.getExtent())}function testMaximum(){var rm=new goog.ui.RangeModel;rm.setMaximum(50);assertEquals(50,rm.getMaximum());reset(rm);rm.setValue(5);rm.setExtent(10);rm.setMinimum(50);rm.setMaximum(40);assertEquals(40,rm.getMaximum());assertEquals(0,rm.getExtent());assertEquals(40,rm.getValue());assertEquals(40,rm.getMinimum());reset(rm);rm.setExtent(10);rm.setValue(50);rm.setMaximum(40);assertEquals(40,rm.getMaximum());assertEquals(30,rm.getValue());reset(rm);rm.setExtent(10);rm.setValue(90);rm.setMaximum(95);assertEquals(95,rm.getMaximum());assertEquals(10,rm.getExtent());assertEquals(85,rm.getValue());reset(rm,3);rm.setMaximum(50);assertEquals(51,rm.getMaximum());reset(rm,3);rm.setValue(5);rm.setExtent(10);rm.setMinimum(50);rm.setMaximum(40);assertEquals(41,rm.getMaximum());assertEquals(0,rm.getExtent());assertEquals(41,rm.getValue());assertEquals(41,rm.getMinimum());reset(rm,3);rm.setExtent(10);rm.setValue(50);rm.setMaximum(40);assertEquals(39,rm.getMaximum());assertEquals(30,rm.getValue());reset(rm,3);rm.setExtent(10);rm.setValue(90);rm.setMaximum(95);assertEquals(96,rm.getMaximum());assertEquals(87,rm.getValue());assertEquals(9,rm.getExtent())}function testExtent(){var rm=new goog.ui.RangeModel;rm.setExtent(10);assertEquals(10,rm.getExtent());rm.setExtent(-10);assertEquals(0,rm.getExtent());rm.setValue(50);rm.setExtent(100);assertEquals(50,rm.getExtent());assertEquals(50,rm.getValue());reset(rm,3);rm.setExtent(10);assertEquals(9,rm.getExtent());rm.setExtent(-10);assertEquals(0,rm.getExtent());rm.setValue(50);rm.setExtent(100);assertEquals(48,rm.getExtent());assertEquals(51,rm.getValue())}function testRoundToStep(){var rm=new goog.ui.RangeModel;rm.setStep(.5);assertEquals(1,rm.roundToStep(1));assertEquals(.5,rm.roundToStep(.5));assertEquals(1,rm.roundToStep(.75));assertEquals(.5,rm.roundToStep(.74))}function testRoundToStepWithMin(){var rm=new goog.ui.RangeModel;rm.setStep(.5);rm.setMinimum(.25);assertEquals(1.25,rm.roundToStepWithMin(1));assertEquals(.75,rm.roundToStepWithMin(.5));assertEquals(.75,rm.roundToStepWithMin(.75));assertEquals(.75,rm.roundToStepWithMin(.74))}