goog.provide("goog.ui.tree.TreeControlTest");goog.setTestOnly("goog.ui.tree.TreeControlTest");goog.require("goog.dom");goog.require("goog.testing.jsunit");goog.require("goog.ui.tree.TreeControl");function makeATree(){var tree=new goog.ui.tree.TreeControl("root"),testData=["A",[["AA",[["AAA",[]],["AAB",[]]]],["AB",[["ABA",[]],["ABB",[]]]]]];createTreeFromTestData(tree,testData,3);tree.render(goog.dom.getElement("treeContainer"));return tree}function createTreeFromTestData(node,data,maxLevels){node.setText(data[0]);if(0>maxLevels){return}for(var children=data[1],i=0;i<children.length;i++){var child=children[i],childNode=node.getTree().createNode("");node.add(childNode);createTreeFromTestData(childNode,child,maxLevels-1)}}function testIndent(){var tree=makeATree();tree.expandAll();var node=tree.getChildren()[0].getChildren()[0];assertEquals("AAA",node.getHtml());assertNotNull(node.getElement());assertEquals("19px",node.getRowElement().style.paddingLeft);assertEquals(2,node.getDepth());var newParent=node.getNextSibling();assertEquals("AAB",newParent.getHtml());assertEquals(2,newParent.getDepth());newParent.add(node);assertEquals(newParent,node.getParent());assertEquals(node,newParent.getChildren()[0]);assertEquals(3,node.getDepth());assertEquals("38px",node.getRowElement().style.paddingLeft)}function testOutdent(){var tree=makeATree();tree.expandAll();var node=tree.getChildren()[0].getChildren()[0];assertEquals("AAA",node.getHtml());assertNotNull(node.getElement());assertEquals("19px",node.getRowElement().style.paddingLeft);assertEquals(2,node.getDepth());var newParent=tree;assertEquals("A",newParent.getHtml());assertEquals(0,newParent.getDepth());newParent.add(node);assertEquals(newParent,node.getParent());assertEquals(node,newParent.getChildren()[2]);assertEquals(1,node.getDepth());assertEquals("0px",node.getRowElement().style.paddingLeft)}