goog.provide("goog.ui.tree.TreeNode");goog.require("goog.ui.tree.BaseNode");goog.ui.tree.TreeNode=function(content,opt_config,opt_domHelper){goog.ui.tree.BaseNode.call(this,content,opt_config,opt_domHelper)};goog.inherits(goog.ui.tree.TreeNode,goog.ui.tree.BaseNode);goog.ui.tree.TreeNode.prototype.getTree=function(){if(this.tree){return this.tree}var parent=this.getParent();if(parent){var tree=parent.getTree();if(tree){this.setTreeInternal(tree);return tree}}return null};goog.ui.tree.TreeNode.prototype.getCalculatedIconClass=function(){var expanded=this.getExpanded(),expandedIconClass=this.getExpandedIconClass();if(expanded&&expandedIconClass){return expandedIconClass}var iconClass=this.getIconClass();if(!expanded&&iconClass){return iconClass}var config=this.getConfig();if(this.hasChildren()){if(expanded&&config.cssExpandedFolderIcon){return config.cssTreeIcon+" "+config.cssExpandedFolderIcon}else if(!expanded&&config.cssCollapsedFolderIcon){return config.cssTreeIcon+" "+config.cssCollapsedFolderIcon}}else{if(config.cssFileIcon){return config.cssTreeIcon+" "+config.cssFileIcon}}return""};