goog.provide("goog.ui.TriStateMenuItemRenderer");goog.require("goog.asserts");goog.require("goog.dom.classlist");goog.require("goog.ui.MenuItemRenderer");goog.ui.TriStateMenuItemRenderer=function(){goog.ui.MenuItemRenderer.call(this)};goog.inherits(goog.ui.TriStateMenuItemRenderer,goog.ui.MenuItemRenderer);goog.addSingletonGetter(goog.ui.TriStateMenuItemRenderer);goog.ui.TriStateMenuItemRenderer.CSS_CLASS=goog.getCssName("goog-tristatemenuitem");goog.ui.TriStateMenuItemRenderer.prototype.decorate=function(item,element){element=goog.ui.TriStateMenuItemRenderer.superClass_.decorate.call(this,item,element);this.setCheckable(item,element,!0);goog.asserts.assert(element);if(goog.dom.classlist.contains(element,goog.getCssName(this.getCssClass(),"fully-checked"))){item.setCheckedState(goog.ui.TriStateMenuItem.State.FULLY_CHECKED)}else if(goog.dom.classlist.contains(element,goog.getCssName(this.getCssClass(),"partially-checked"))){item.setCheckedState(goog.ui.TriStateMenuItem.State.PARTIALLY_CHECKED)}else{item.setCheckedState(goog.ui.TriStateMenuItem.State.NOT_CHECKED)}return element};goog.ui.TriStateMenuItemRenderer.prototype.getCssClass=function(){return goog.ui.TriStateMenuItemRenderer.CSS_CLASS};