goog.require("axs.AuditRules");goog.require("axs.browserUtils");goog.require("axs.constants.Severity");axs.AuditRules.addRule({name:"audioWithoutControls",heading:"Audio elements should have controls",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_audio_01",severity:axs.constants.Severity.WARNING,relevantElementMatcher:function(element){return axs.browserUtils.matchSelector(element,"audio[autoplay]")},test:function(audio){var controls=audio.querySelectorAll("[controls]");return!controls.length&&3<audio.duration},code:"AX_AUDIO_01"});