goog.require("axs.AuditRules");goog.require("axs.browserUtils");goog.require("axs.constants.Severity");goog.require("axs.dom");goog.require("axs.utils");axs.AuditRules.addRule({name:"controlsWithoutLabel",heading:"Controls and media elements should have labels",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_text_01",severity:axs.constants.Severity.SEVERE,relevantElementMatcher:function(element){var controlsSelector=["input:not([type=\"hidden\"]):not([disabled])","select:not([disabled])","textarea:not([disabled])","button:not([disabled])","video:not([disabled])"].join(", "),isControl=axs.browserUtils.matchSelector(element,controlsSelector);if(!isControl||"presentation"==element.getAttribute("role"))return!1;if(0<=element.tabIndex)return!0;for(var parent=axs.dom.parentElement(element);null!=parent;parent=axs.dom.parentElement(parent)){if(axs.utils.elementIsAriaWidget(parent))return!1}return!0},test:function(control,flags){if(flags.hidden)return!1;if("input"==control.tagName.toLowerCase()&&"button"==control.type&&control.value.length){return!1}if("button"==control.tagName.toLowerCase()){var innerText=control.textContent.replace(/^\s+|\s+$/g,"");if(innerText.length)return!1}if(axs.utils.hasLabel(control))return!1;var textAlternatives=axs.properties.findTextAlternatives(control,{});if(null===textAlternatives||""===textAlternatives.trim())return!0;return!1},code:"AX_TEXT_01",ruleName:"Controls and media elements should have labels"});