goog.require("axs.AuditRules");goog.require("axs.browserUtils");goog.require("axs.constants.Severity");goog.require("axs.properties");goog.require("axs.utils");axs.AuditRules.addRule({name:"imagesWithoutAltText",heading:"Images should have a text alternative or presentational role",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_text_02",severity:axs.constants.Severity.WARNING,relevantElementMatcher:function(element,flags){return axs.browserUtils.matchSelector(element,"img")&&!flags.hidden},test:function(image){var imageIsPresentational=image.hasAttribute("alt")&&""==image.alt||"presentation"==image.getAttribute("role");if(imageIsPresentational)return!1;var textAlternatives={};axs.properties.findTextAlternatives(image,textAlternatives);var numTextAlternatives=Object.keys(textAlternatives).length;if(0==numTextAlternatives)return!0;return!1},code:"AX_TEXT_02"});