goog.require("axs.AuditRules");goog.require("axs.constants.Severity");goog.require("axs.properties");goog.require("axs.utils");axs.AuditRules.addRule({name:"lowContrastElements",heading:"Text elements should have a reasonable contrast ratio",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_color_01",severity:axs.constants.Severity.WARNING,relevantElementMatcher:function(element,flags){return!flags.disabled&&axs.properties.hasDirectTextDescendant(element)},test:function(element){var style=window.getComputedStyle(element,null),contrastRatio=axs.utils.getContrastRatioForElementWithComputedStyle(style,element);return contrastRatio&&axs.utils.isLowContrast(contrastRatio,style)},code:"AX_COLOR_01"});