goog.require("axs.AuditRules");goog.require("axs.browserUtils");goog.require("axs.properties");goog.require("axs.utils");axs.AuditRules.addRule({name:"mainRoleOnInappropriateElement",heading:"role=main should only appear on significant elements",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_aria_05",severity:axs.constants.Severity.WARNING,relevantElementMatcher:function(element){return axs.browserUtils.matchSelector(element,"[role~=main]")},test:function(element){if(axs.utils.isInlineElement(element))return!0;var computedTextContent=axs.properties.getTextFromDescendantContent(element);if(!computedTextContent||50>computedTextContent.length)return!0;return!1},code:"AX_ARIA_05"});