goog.require("axs.AuditRules");goog.require("axs.browserUtils");goog.require("axs.constants.Severity");goog.require("axs.utils");axs.AuditRules.addRule({name:"nonExistentRelatedElement",heading:"Attributes which refer to other elements by ID should refer to elements which exist in the DOM",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_html_03",severity:axs.constants.Severity.SEVERE,opt_requires:{idRefs:!0},relevantElementMatcher:function(element,flags){return 0<flags.idrefs.length},test:function(element,flags){var idRefs=flags.idrefs,missing=idRefs.some(function(id){var refElement=document.getElementById(id);return!refElement});return missing},code:"AX_HTML_03"});