goog.require("axs.AuditRules");goog.require("axs.constants.Severity");axs.AuditRules.addRule({name:"pageWithoutTitle",heading:"The web page should have a title that describes topic or purpose",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_title_01",severity:axs.constants.Severity.WARNING,relevantElementMatcher:function(element){return"html"==element.tagName.toLowerCase()},test:function(scope){var head=scope.querySelector("head");if(!head)return!0;var title=head.querySelector("title");if(!title)return!0;return!title.textContent},code:"AX_TITLE_01"});