goog.require("axs.AuditRules");goog.require("axs.browserUtils");goog.require("axs.constants.Severity");goog.require("axs.utils");axs.AuditRules.addRule({name:"roleTooltipRequiresDescribedby",heading:"Elements with role=tooltip should have a corresponding element with aria-describedby",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_aria_02",severity:axs.constants.Severity.SEVERE,relevantElementMatcher:function(element,flags){return axs.browserUtils.matchSelector(element,"[role=tooltip]")&&!flags.hidden},test:function(element){var referrers=axs.utils.getAriaIdReferrers(element,"aria-describedby");return!referrers||0===referrers.length},code:"AX_TOOLTIP_01"});