goog.require("axs.AuditRules");goog.require("axs.browserUtils");goog.require("axs.constants.Severity");(function(){function labeledByATab(element){if(element.hasAttribute("aria-labelledby")){var labelingElements=document.querySelectorAll("#"+element.getAttribute("aria-labelledby"));return 1===labelingElements.length&&"tab"===labelingElements[0].getAttribute("role")}return!1}function controlledByATab(element){var controlledBy=document.querySelectorAll("[role=\"tab\"][aria-controls=\""+element.id+"\"]");return element.id&&1===controlledBy.length}axs.AuditRules.addRule({name:"uncontrolledTabpanel",heading:"A tabpanel should be related to a tab via aria-controls or aria-labelledby",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_aria_13",severity:axs.constants.Severity.WARNING,relevantElementMatcher:function(element){return axs.browserUtils.matchSelector(element,"[role=\"tabpanel\"]")},test:function(element){return!(controlledByATab(element)||labeledByATab(element))},code:"AX_ARIA_13"})})();