goog.require("axs.AuditRules");goog.require("axs.browserUtils");goog.require("axs.constants.Severity");axs.AuditRules.addRule({name:"videoWithoutCaptions",heading:"Video elements should use <track> elements to provide captions",url:"https://github.com/GoogleChrome/accessibility-developer-tools/wiki/Audit-Rules#ax_video_01",severity:axs.constants.Severity.WARNING,relevantElementMatcher:function(element){return axs.browserUtils.matchSelector(element,"video")},test:function(video){var captions=video.querySelectorAll("track[kind=captions]");return!captions.length},code:"AX_VIDEO_01"});