module("AriaRoleNotScoped");test("Scope role present",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("div"));container.setAttribute("role","list");for(var i=0,item;4>i;i++){item=container.appendChild(document.createElement("span"));item.setAttribute("role","listitem")}var config={ruleName:"ariaRoleNotScoped",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("Scope role implicitly present",function(assert){for(var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("ol")),i=0,item;4>i;i++){item=container.appendChild(document.createElement("span"));item.setAttribute("role","listitem")}var config={ruleName:"ariaRoleNotScoped",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("Scope role present via aria-owns",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("div")),siblingContainer=fixture.appendChild(document.createElement("div")),ids=[];container.setAttribute("role","list");for(var i=0;4>i;i++){var id=ids[i]="item"+i,item=siblingContainer.appendChild(document.createElement("span"));item.setAttribute("role","listitem");item.setAttribute("id",id)}container.setAttribute("aria-owns",ids.join(" "));equal(container.childNodes.length,0,"container should have no child nodes since we're checking use of aria-owns");var config={ruleName:"ariaRoleNotScoped",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("Scope role missing",function(assert){for(var fixture=document.getElementById("qunit-fixture"),expected=[],i=0,item;4>i;i++){item=fixture.appendChild(document.createElement("span"));item.setAttribute("role","listitem");expected.push(item)}var config={ruleName:"ariaRoleNotScoped",expected:axs.constants.AuditResult.FAIL,elements:expected};assert.runRule(config)});test("Scope role present - tablist",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("ul"));container.setAttribute("role","tablist");for(var i=0,item;4>i;i++){item=container.appendChild(document.createElement("li"));item.setAttribute("role","tab")}var config={ruleName:"ariaRoleNotScoped",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("Scope role missing - tab",function(assert){for(var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("ul")),expected=[],i=0,item;4>i;i++){item=container.appendChild(document.createElement("li"));item.setAttribute("role","tab");expected.push(item)}var config={ruleName:"ariaRoleNotScoped",expected:axs.constants.AuditResult.FAIL,elements:expected};assert.runRule(config)});