module("FocusableElementNotVisibleAndNotAriaHidden",{setup:function(){var fixture=document.createElement("div");document.getElementById("qunit-fixture").appendChild(fixture);this.fixture_=fixture;fixture.style.top=0;fixture.style.left=0}});test("a focusable element that is visible passes the audit",function(assert){var input=document.createElement("input");this.fixture_.appendChild(input);var config={scope:this.fixture_,ruleName:"focusableElementNotVisibleAndNotAriaHidden",elements:[],expected:axs.constants.AuditResult.PASS};assert.runRule(config)});test("a focusable element that is hidden fails the audit",function(assert){var input=document.createElement("input");input.style.opacity="0";this.fixture_.appendChild(input);var config={scope:this.fixture_,ruleName:"focusableElementNotVisibleAndNotAriaHidden",elements:[input],expected:axs.constants.AuditResult.FAIL};assert.runRule(config)});test("an element with negative tabindex and empty computed text is ignored",function(assert){var emptyDiv=document.createElement("div");emptyDiv.tabIndex="-1";this.fixture_.appendChild(emptyDiv);var config={scope:this.fixture_,ruleName:"focusableElementNotVisibleAndNotAriaHidden",expected:axs.constants.AuditResult.NA};assert.runRule(config)});