module("MainRoleOnInappropriateElement");test("No role=main -> no relevant elements",function(assert){var fixture=document.getElementById("qunit-fixture"),div=document.createElement("div");fixture.appendChild(div);var config={ruleName:"mainRoleOnInappropriateElement",expected:axs.constants.AuditResult.NA};assert.runRule(config)});test("role=main on empty element === fail",function(assert){var fixture=document.getElementById("qunit-fixture"),div=document.createElement("div");div.setAttribute("role","main");fixture.appendChild(div);var config={ruleName:"mainRoleOnInappropriateElement",expected:axs.constants.AuditResult.FAIL,elements:[div]};assert.runRule(config)});test("role=main on element with textContent < 50 characters === pass",function(assert){var fixture=document.getElementById("qunit-fixture"),div=document.createElement("div");div.setAttribute("role","main");div.textContent="Lorem ipsum dolor sit amet.";fixture.appendChild(div);var config={ruleName:"mainRoleOnInappropriateElement",expected:axs.constants.AuditResult.FAIL,elements:[div]};assert.runRule(config)});test("role=main on element with textContent >= 50 characters === pass",function(assert){var fixture=document.getElementById("qunit-fixture"),div=document.createElement("div");div.setAttribute("role","main");div.textContent="Lorem ipsum dolor sit amet, consectetur cras amet.";fixture.appendChild(div);var config={ruleName:"mainRoleOnInappropriateElement",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("role=main on inline element === fail",function(assert){var fixture=document.getElementById("qunit-fixture"),span=document.createElement("span");span.setAttribute("role","main");span.textContent="Lorem ipsum dolor sit amet, consectetur cras amet.";fixture.appendChild(span);var config={ruleName:"mainRoleOnInappropriateElement",expected:axs.constants.AuditResult.FAIL,elements:[span]};assert.runRule(config)});