module("multipleLabelableElementsPerLabel");test("one labelable element within a label tag",function(assert){var fixture=document.getElementById("qunit-fixture"),label=document.createElement("label"),input=document.createElement("input");fixture.appendChild(label);label.appendChild(input);var config={ruleName:"multipleLabelableElementsPerLabel",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config,"passes the audit with no matching elements")});test("multiple labelable elements within a label tag",function(assert){var fixture=document.getElementById("qunit-fixture"),label=document.createElement("label"),input1=document.createElement("input"),input2=document.createElement("input");fixture.appendChild(label);label.appendChild(input1);label.appendChild(input2);var config={ruleName:"multipleLabelableElementsPerLabel",expected:axs.constants.AuditResult.FAIL,elements:[label]};assert.runRule(config,"fails the audit on that label")});